  -- The Mountain-hold / The Hiddenhoard of Abnankâra -- Explorer of the Hiddenhoard
  [1879443581] = {
      [1] = {
      ["NAME"] = "Jókulskeld"; 
      ["PROGRESS"] = "Discover Jókulskeld"; 
      ["LORE"] = "Presumed to be the source of the river Kurdushâlak which flows through the Clovengap, Jókulskeld was frozen solid by the breath of Hrímil Frost-heart. Far above this well of ice, the ruins of ancient causeways lead to the Hiddenhoard of Abnankâra."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "49.99N"; 
          ["LONG"] = "45.31E"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "The Hiddenhoard of Abnankâra"; 
      ["PROGRESS"] = "Discover the Hiddenhoard of Abnankâra"; 
      ["LORE"] = "Deep within the caverns of the Clovengap lies Abnankâra, the Hiddenhoard. Wrought in secret by Mótsog and the Greymauls during their rebellion against the Longbeards in the Elder Days, it was to be a grand treasury—the jewel of a new kingdom within Mount Gundabad. After the defeat of Mótsog and the Greymauls at the Sunder-battle, Abnankâra was abandoned and laid forgotten until its discovery late in the Third Age."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "51.07N"; 
          ["LONG"] = "45.31E"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Ibkêrmazal"; 
      ["PROGRESS"] = "Discover Ibkêrmazal"; 
      ["LORE"] = "Within the Hiddenhoard of Abnankâra, Ibkêrmazal served as both a grand armoury and a bulwark against those who would seek to plunder the hoards of the Greymauls. By Mótsog's order, the hall was divided into three sections—each of which were flanked by spear-traps and hidden barracks—to separate and overwhelm invaders and, in direst need, to puncture the soft underbellies of Dragon-kind."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "48.13N"; 
          ["LONG"] = "46.90E"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Akrazulgibshîn"; 
      ["PROGRESS"] = "Discover Akrazulgibshîn"; 
      ["LORE"] = "Although it would never truly fulfil the promise of the grand treasury that Mótsog so desired, Akrazulgibshîn became a repository for all the riches of Mount Gundabad pillaged by the Greymauls after Durin led many of the Longbeards to Khazad-dûm. After the Sunder-battle and the ruinous end of the Greymaul rebellion, Durin laid the war-hammer of Mótsog's last son, Módvit, among its hoards and evermore abandoned the Hiddenhoard of Abnankâra."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "49.45N"; 
          ["LONG"] = "46.91E"; 
        }; 
      }; 
      };
  };
