  [1879450862] = {
      [1] = {
      ["NAME"] = "Corner Stone"; 
      ["PROGRESS"] = "Discover an old cornerstone"; 
      ["LORE"] = "These old stones may have borne witness to Sauron’s onslaught, but they still hold fast to happier memories. Weddings, personal centenaries, and ceremonies of all kinds took place in this once-elegant pavilion."; 
      ["POINTS"] = {
