      [2] = {
      ["NAME"] = "Curious Pendant"; 
      ["PROGRESS"] = "Discover a curious pendant"; 
      ["LORE"] = "'My love, if you are reading this, then you have succeeded at my little treasure hunt and claimed the prize! I had this necklace made to your tastes. I look forward to seeing you wear it.'"; 
