  [1879450926] = {
      [1] = {
      ["NAME"] = "Oath Plaque"; 
      ["PROGRESS"] = "Discover the Oath Plaque"; 
      ["LORE"] = "Though worn and weathered by time, you are able to make out a few words inscribed upon this plaque. They seem to allude to the oaths that were sworn here hundreds of years ago, when the Hill-folk were forced to submit to Dúnedain kings."; 
      ["POINTS"] = {
