      [2] = {
      ["NAME"] = "Galdir's Dell"; 
      ["PROGRESS"] = "Find Galdir's Dell"; 
      ["LORE"] = "The encampment that once stood in Galdir's Dell is now burned and unrecognizable. Wargs pad among the scorched wooden structures, and their howls linger on the breezes that sweep over the river."; 
