  -- Eastern King's Gondor / Reclaiming Lossarnach
  [1879464437] = {
      [1] = {
      ["NAME"] = "Arnach"; 
      ["PROGRESS"] = "Find Arnach"; 
      ["LORE"] = "Lord Forlong, the ruler of Lossarnach, kept a hall in the village of Arnach until he rode to Minas Tirith during the War of the Ring. He perished in the fighting on the Pelennor and now his hall stands empty, the people of Arnach doing the best they can to go on without their much-loved lord. Arnach remains a bustling settlement for merchants and craftsmen, but the memory of the Haradrim attacks during the previous months remains painful."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "76.55S"; 
          ["LONG"] = "24.40W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Imloth Melui"; 
      ["PROGRESS"] = "Find Imloth Melui"; 
      ["LORE"] = "Imloth Melui, the Vale of Sweet Flowers, is the home of Lady Vanyalos, sister of the Steward Denethor and wife of Forlong, Lord of Lossarnach. Both of these men perished during the war, one inside the walls of the White City and the other without, but the wounds of their passing lie especially deep upon Vanyalos, for she dreamed it would happen and feels the burden of failing to prevent it.\n\nImloth Melui provides many of the healers who work at the Houses of Healing in Minas Tirith, including the woman Ioreth, who knows a little about an impressive array of things, and an impressive amount about very little."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "73.20S"; 
          ["LONG"] = "24.79W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Nan Harlond"; 
      ["PROGRESS"] = "Find Nan Harlond"; 
      ["LORE"] = "The fields and farms of Nan Harlond stand on either side of the road below the south-harbour of Gondor, no longer threatened by the armies of Sauron but still endangered by occasional foes left behind by their defeated allies. The natural beauty of Lossarnach fights to emerge following the War of the Ring, and the flowers here are a testament to its resilience."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "74.29S"; 
          ["LONG"] = "21.04W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Rammas Echor"; 
      ["PROGRESS"] = "Find the Rammas Echor"; 
      ["LORE"] = "The great wall that is the Rammas Echor encircles Minas Tirith and the Pelennor Fields. During the war, the wall was breached in several places and the forces of Sauron streamed through to lay siege to the White City. Now, dwarves and Gondorian labourers work to repair the damage and shore up the defences once again, though all hope the Rammas will not be needed again for many years."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "72.14S"; 
          ["LONG"] = "18.75W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "The Harlond"; 
      ["PROGRESS"] = "Find the Harlond"; 
      ["LORE"] = "The Harlond was held by Orcs of Mordor in the days before the Battle of Pelennor Fields, and the creatures burned as many of the Gondorian ships as they could. When Corsair ships flying black sails came up the river from the south, the Orcs were taken by surprise when the banner of the King was raised, revealing the vessels to be commanded by Aragorn and the allies of Gondor. Their arrival on the field of battle proved instrumental in the defence of Minas Tirith and the defeat of Sauron's forces.\n\nToday, the folk of Gondor and dwarves from the White Mountains work to repair and rebuild the south-harbour, hoping that trade and goods will once again flow freely upon the river to ports both near and far."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "72.72S"; 
          ["LONG"] = "20.01W"; 
        }; 
      }; 
      };
  };
  -- Eastern King's Gondor / Reclaiming Upper Lebennin
  [1879464470] = {
      [1] = {
      ["NAME"] = "Minlos"; 
      ["PROGRESS"] = "Find Minlos"; 
      ["LORE"] = "The beacon of Minlos is the nearest to Minas Tirith, and was seized by Haradrim raiders during the war as they attempted to move against the village of Arnach. Now the beacon has been extinguished and folk of nearby farms hope it will remain unlit for many years, a signal the danger has passed."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "76.83S"; 
          ["LONG"] = "25.96W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Halach"; 
      ["PROGRESS"] = "Find Halach"; 
      ["LORE"] = "The small harbour of Halach was abandoned to the Haradrim during the war, but the defeat of Sauron has allowed the townsfolk to return and begin the long process of rebuilding their lives and homes."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "82.26S"; 
          ["LONG"] = "26.21W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Glaniath"; 
      ["PROGRESS"] = "Find Glaniath"; 
      ["LORE"] = "The small farming community of Glaniath was threatened by Haradrim raiders during the war, and now the people try to pick up the pieces of their lives in the aftermath. Some folk here believe Sirgon's captivity by the Haradrim has made him unfit to rule and his son Faltharan should become Lord of Lebennin in his stead."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "80.62S"; 
          ["LONG"] = "29.70W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Tumladen"; 
      ["PROGRESS"] = "Find Tumladen"; 
      ["LORE"] = "Tumladen is the great refuge of Gondor, similar in kind to Helm's Deep in the kingdom of Rohan. During the war, many folk took shelter in Tumladen, including one of Steward Denethor's prickly sisters, the opinionated Lady Terenis. Much to the relief of Captain Imladir, who is responsible for the defence of Tumladen, the Lady Terenis has now returned to Minas Tirith. The sanctuary is no longer crowded with displaced folk of Gondor."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "76.84S"; 
          ["LONG"] = "29.35W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Pelargir"; 
      ["PROGRESS"] = "Find Pelargir"; 
      ["LORE"] = "The port city of Pelargir was the site of the pivotal battle against the Corsairs of Umbar during the War of the Ring, when Aragorn commanded the Army of the Dead to seize their ships. Balakhôr the Scourge met his end aboard his grand vessel, the Night-jewel, and it is hoped his death will spell the end of the threat posed by the Heirs of Castamir.\n\nThe Gondorian fleet is in shambles following the War of the Ring, and it will be the task of Sirgon, Lord of Lebennin, to rebuild the kingdom's ships and sea-power as quickly as possible. To that end, the city of Pelargir is filled with the sound of hammers and axes at all hours of the day and night."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "84.01S"; 
          ["LONG"] = "33.18W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Sirthanc"; 
      ["PROGRESS"] = "Find Sirthanc"; 
      ["LORE"] = "The beacon of Sirthanc was lit during the war to summon warriors to defend Minas Tirith, but it is now quenched. If the fortune of Gondor holds, it will remain extinguished for many years. It was at Sirthanc that Lord Sirgon was rescued from the captivity of the Haradrim, and now some of his folk believe he should be allowed to step down as Lord of Lebennin so he may heal the wounds of his mind and body."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "79.35S"; 
          ["LONG"] = "31.96W"; 
        }; 
      }; 
      };
  };
