  [1879464488] = {
      [1] = {
      ["NAME"] = "Erynos"; 
      ["PROGRESS"] = "Find Erynos"; 
      ["LORE"] = "The settlement of Erynos was the site of one of the stranger occurrences of the war, when a number of Corsair raiders were discovered outside the walls of town, apparently drowned. Some folk believe this was the work of the Five Sisters of Lebennin, river-maidens thought to protect the lands through which their rivers flow, but some say they are only figures of myth.\n\nToday the folk of Erynos work to restore their lives and aid those of their neighbours who suffered during the war."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "80.08S"; 
          ["LONG"] = "32.80W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Ost Anglebed"; 
      ["PROGRESS"] = "Find Ost Anglebed"; 
      ["LORE"] = "The large city of Ost Anglebed is home to the keepers of the beacon of Amon Anglebed, which was lit during the War of the Ring. Unlike the other beacons of Gondor, the beacon here remains lit despite the arrival of the King's peace. Now the guards maintain a vigil over the townsfolk, fearful that some allies of disloyal Malthellim may think to continue his work and turn control of the town over to foes of Gondor."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "78.98S"; 
          ["LONG"] = "39.14W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Malbarth"; 
      ["PROGRESS"] = "Find Malbarth"; 
      ["LORE"] = "The small village of Malbarth escaped much of the fighting during the war, but tidings from other areas of Gondor filled the townsfolk here with trepidation and fear that their homes would not remain safe for long. Even now there are whispers that some Corsairs fled from the battle at Pelargir and hide now in the wilderness, awaiting their chance to seize the town."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "80.14S"; 
          ["LONG"] = "42.38W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Linhir"; 
      ["PROGRESS"] = "Find Linhir"; 
      ["LORE"] = "The city of Linhir spans the River Gilrain, and during the War of the Ring the sky here was darkened by foul craft of Sauron. The sons of Comeldir organized the defences, and when the fighting at last was ended, the people stood strong, less divided than their city. Today, with the threat of war behind them and the skies bright and clear once again, the folk of Linhir have returned to their lives."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "81.41S"; 
          ["LONG"] = "45.43W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Sarnhad"; 
      ["PROGRESS"] = "Find Sarnhad"; 
      ["LORE"] = "This small village on the River Serni escaped most of the fighting during the war, but the report of Corsairs and half-trolls attacking other nearby towns filled the residents with dread, as did the darkened skies brought on by the Dawnless Day. Today, the folk of Sarnhad seek to return to their ordinary lives."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "81.59S"; 
          ["LONG"] = "43.90W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Zarsatrâd"; 
      ["PROGRESS"] = "Find Zarsatrâd"; 
      ["LORE"] = "Nestled in the forest south of the dwarf-stronghold of Furukzahar, Zarsatrâd serves as an outpost for the Temámir dwarves who live now in the White Mountains. They work to transport quarried stone to various locations in Gondor, eager to help their neighbours repair and rebuild following the War of the Ring... for a reasonable price."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "77.80S"; 
          ["LONG"] = "34.56W"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Furukzahar"; 
      ["PROGRESS"] = "Find Furukzahar"; 
      ["LORE"] = "The stronghold of Furukzahar is a bastion of safety for the Temámir Dwarves of the White Mountains. Under the rule of their leader Hámisar, dwarves from any clan are welcome to visit, but relations with the people of Gondor are not especially warm at the moment. Hámisar's decision to shut the doors of Furukzahar and not participate in the War of the Ring has not endeared the dwarves to the residents of Lebennin."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "70.34S"; 
          ["LONG"] = "35.35W"; 
        }; 
      }; 
      };
  };
