  [1879466366] = {
      [1] = {
      ["NAME"] = "Fennas Dûr"; 
      ["PROGRESS"] = "Find Fennas Dûr"; 
      ["LORE"] = "From the threshold of the Dark Door in the shadows of the Dimholt of Rohan, the Paths of the Dead carve a great tunnel beneath the Haunted Mountain, Dwimorberg, to Fennas Dûr in the Blackroot Vale of Gondor. It was through Fennas Dûr that many of the Dead Men of Dunharrow followed King Elessar to the Stone of Erech and to the fulfillment of their oath at Pelargir. Alas, the unrepentant few who refused the summons twice-forsook their oath, condemned to their fate anew by their own will. Now, these Ruthless Dead still linger, haunting the shadows of the Blackroot Vale—uncertain and perhaps uncaring whether the curse Isildur levied upon them will ever be lifted."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "53.15S"; 
          ["LONG"] = "66.85W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Morlad"; 
      ["PROGRESS"] = "Find Morlad"; 
      ["LORE"] = "Against the southern slopes of the White Mountains along the River Morthond lies the seat of the Blackroot Vale, the walled town of Morlad. Atop its winding roads sits the estate of the Lord of the Blackroot Vale, Duinhir, and his wife, Rosfin. When Gondor was called to war, Lord Duinhir and his sons, Duilin and Derufin, led some five hundred bowmen to Minas Tirith to defend the city against the armies of Mordor. After the war was ended and Lord Duinhir and his men returned to Morlad, news spread of the deaths of his sons and of the attempt to lay the blame for the murder of a Haradrim emissary upon him. Despite Gondor's victory, Morlad has become a grim place, and many of its people believe they have been cast aside by King Elessar in the aftermath of the War of the Ring."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "56.84S"; 
          ["LONG"] = "67.55W"; 
        }; 
      }; 
      };
      [3] = {
