      [9] = {
      ["NAME"] = "Hill of Erech"; 
      ["PROGRESS"] = "Find the Hill of Erech"; 
      ["LORE"] = "Rising near the mouth of the Blackroot Vale, the Hill of Erech has been the site of a great deal of history in Gondor. In the Second Age, it was there that Isildur set the Stone of Erech, it was there that the Men of the Mountain swore their oath to aid Gondor, and it was there that they betrayed Isildur and were cursed. It was for this reason that the Hill of Erech came to be ill-regarded and even feared by the folk of the Blackroot Vale, who believed that the Dead haunted it."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "58.26S"; 
          ["LONG"] = "64.86W"; 
        }; 
      }; 
      };
      [10] = {
      ["NAME"] = "Stone of Erech"; 
      ["PROGRESS"] = "Find the Stone of Erech"; 
      ["LORE"] = "Borne away from the destruction of Númenor, the Stone of Erech sits atop the Hill of Erech in the Blackroot Vale. In truth, the Stone of Erech is one of the seven Vandassari, the Oath-stones, and is named Orossar, but this has been forgotten to all but the most learned of scholars in Middle-earth. It was upon the Stone of Erech that the Men of the Mountains long ago swore an oath to Isildur to aid Gondor in the war against the Dark Lord, Sauron. However, when they refused to honour their oath, Isildur cursed them, condemning them to dwindle and diminish into the Sleepless Dead. Although many of the Dead have now departed with the fulfillment of their oath, the Stone of Erech still rests atop its hill and forever serves as a reminder of the betrayal of the Men of the Mountains."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "58.05S"; 
          ["LONG"] = "65.05W"; 
        }; 
      }; 
      };
      [11] = {
      ["NAME"] = "Azruthor's Rue"; 
      ["PROGRESS"] = "Find Azruthor's Rue"; 
      ["LORE"] = "Under the command of Balakhôr the Scourge, one of the Heirs of Castamir, Azruthor, constructed a blockade at Tarlang's Crown. It was there that the Scourge's plans to sack Anfalas and claim its coasts for Umbar were to unfold, but Azruthor failed in his attempt to conquer the lands of Gondor. With his company of Corsairs defeated and his seeming alliance with the Ruthless Dead broken, he met his end at the hands of brave adventurers and the blockade was destroyed. In the aftermath of the War of the Ring, all that remains of Azruthor's grand scheme is a half-ruined encampment amidst a splintered blockade in Tarlang's Crown."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "67.28S"; 
          ["LONG"] = "68.88W"; 
        }; 
