      [2] = {
      ["NAME"] = "Khataj Mârsa"; 
      ["PROGRESS"] = "Find Khataj Mârsa"; 
      ["LORE"] = "Corsairs have taken up residence here at Khataj Mârsa, giving them easy access to the islands that make up Bakhjâd to the north-west and the large island of Zamarzîr to the south-east."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "7.05S"; 
