      [3] = {
      ["NAME"] = "Bâkhabar"; 
      ["PROGRESS"] = "Find Bâkhabar"; 
      ["LORE"] = "The pestilential creatures known as kergrim are drawn to death and the dying, and there have been both at this site of wrecks and ruins. Many sailors have fallen into the sailor's grave that is Bâkhabar."; 
      ["POINTS"] = {
        [1] = {
