      [5] = {
      ["NAME"] = "Jeb Lipha"; 
      ["PROGRESS"] = "Find Jeb Lipha"; 
      ["LORE"] = "The sound of the ferocious drakes that crawl upon Jeb Lipha carries a great distance over the water, warning those who travel in Zîrar Tarka not to approach this place, which means 'serpent-stone' in the Umbari language."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "0.31N"; 
          ["LONG"] = "132.49W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Dor Pherkha"; 
      ["PROGRESS"] = "Find Dor Pherkha"; 
      ["LORE"] = "Dor Pherkha, the 'cat's cleft,' is located on the southern side of Jeb Khâtel. It may be partially responsible for the island's reputation, for anyone who draws too near the creatures living here are likely to become their prey, and tonight's meal can bring no tales forth from the island."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "0.67S"; 
          ["LONG"] = "134.50W"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Maj Khibi"; 
      ["PROGRESS"] = "Find Maj Khibi"; 
      ["LORE"] = "Maj Khibi, the 'netted grove,' is home to a particularly unpleasant breed of spider. It is said that in years past this place had other denizens, but once the spiders decided to move in none desired to stay for long."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "1.79N"; 
          ["LONG"] = "135.94W"; 
        }; 
      }; 
      };
      [8] = {
      ["NAME"] = "Injakhûr"; 
      ["PROGRESS"] = "Find Injakhûr"; 
      ["LORE"] = "Corsairs camp beside the lagoon decorated with the shipwrecks torn apart by the Grinding Jaws of Shedax Khîn. Is it because the broken hulls remind them of nature's cruelty or the whims of the Valar? Or is it because the newly-created wrecks may provide easy pickings for seekers of treasure? None can say for certain."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "1.83S"; 
          ["LONG"] = "127.90W"; 
        }; 
      }; 
      };
      [9] = {
      ["NAME"] = "Bax Garâkh"; 
      ["PROGRESS"] = "Find Bax Garâkh"; 
      ["LORE"] = "Bax Garâkh, the 'drowning shallows,' are located to the east of Bakhjâd. Despite the possibility of finding valuable treasure among the wrecks torn apart by Shedax Khîn, few dare to trespass. It is said the Dead guard that treasure, and only the very foolish or the very fortunate would risk their wrath."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "4.17S"; 
          ["LONG"] = "127.65W"; 
        }; 
      }; 
      };
      [10] = {
      ["NAME"] = "Ghar Jâdam"; 
      ["PROGRESS"] = "Find Ghar Jâdam"; 
      ["LORE"] = "Ghar Jâdam, the 'grotto of bones,' is located on Zamarzîr, the largest island of the chain that makes up Zîrar Tarka. It is home to jungle-beasts of great size and strength, and the wiser residents of Halrax have learned to give the creatures a wide berth. The less wise among them have gone on to decorate the grotto themselves."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "6.46S"; 
          ["LONG"] = "126.64W"; 
        }; 
      }; 
      };
      [11] = {
      ["NAME"] = "Dar Grekhûl"; 
      ["PROGRESS"] = "Find Dar Grekhûl"; 
      ["LORE"] = "Dar Grekhûl, the 'lizard-home,' can be found on the eastern side of Zamarzîr. The legs of the salamanders that live here are said to be a culinary delicacy, if you can discover the correct means of preparing them. Any of the residents of Halrax who know the secret, however, are not inclined to share it."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "9.28S"; 
          ["LONG"] = "123.45W"; 
        }; 
      }; 
      };
      [12] = {
      ["NAME"] = "Baj Khatûg"; 
      ["PROGRESS"] = "Find Baj Khatûg"; 
      ["LORE"] = "Baj Khatûg, the 'slashing shore,' is a wide beach on the south-eastern island of Zîrar Tarka, nearest to the Cape of Umbar. It is home to large crabs that both pinch and scuttle. Rumour has it their meat is delicious, if you can avoid the pinching and overcome the scuttling."; 
      ["POINTS"] = {
