  [1879474812] = {
      [1] = {
      ["NAME"] = "Grijâti"; 
      ["PROGRESS"] = "Find Grijâti"; 
      ["LORE"] = "Grijâti, the Home of the Stranded, lies on the western shore of the Cape of Umbar. Haradrim remain here, hoping to usurp control from the Kindred of the Coins."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "16.67S"; 
          ["LONG"] = "114.86W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Súlstrok"; 
      ["PROGRESS"] = "Find Súlstrok"; 
      ["LORE"] = "Súlstron, the Raider's Refuge, led by the goblin Corsair Shakfut Slit-throat, is a makeshift encampment at the foot of the mountains of the Cape of Umbar."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "18.62S"; 
          ["LONG"] = "112.41W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Jâghimsa"; 
      ["PROGRESS"] = "Find Jâghimsa"; 
      ["LORE"] = "Jâghimsa, the Wandering Wood, is home to huorns and a fearsome wood-troll herder and lies on the hillside to the north and east of Rakhatâb."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "23.13S"; 
          ["LONG"] = "112.86W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Kan Skûrja"; 
      ["PROGRESS"] = "Find Kan Skûrja"; 
      ["LORE"] = "The Grim Shelter, or Kan Skûrja, is the makeshift home of the nomadic Corsairs who still raid the seas along the Shield Isles and Cape of Umbar."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "19.81S"; 
          ["LONG"] = "109.88W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Ghadir Sûd"; 
      ["PROGRESS"] = "Find Ghadir Sûd"; 
      ["LORE"] = "Ghadir Sûd, the Black Pools, speaks to ancient wellsprings of tar that bubbled up on the land long ago. These bubbling dark springs drew the salamanders that still dwell there today. Though the wellsprings are gone, the salamanders remain."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "20.48S"; 
          ["LONG"] = "109.58W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Kâshtabil"; 
      ["PROGRESS"] = "Find Kâshtabil"; 
      ["LORE"] = "Kâshtabil, the Crawling Tangle, is a burbling swamp filled with driftwood, crabs, and nerbyg. The filthy waters are the source of many ills that plague the nearby village of Khûtra."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "19.21S"; 
          ["LONG"] = "107.15W"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Gherja Mût"; 
      ["PROGRESS"] = "Find Gherja Mût"; 
      ["LORE"] = "In the shadow of The Mâkhda Korbo lies Gherja Mût, the Barrens of the Dead. The risen dead creeping through these ruins are a mix of ancient and the freshly dead creatures reanimated by Sauron's lingering power."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "19.42S"; 
          ["LONG"] = "103.97W"; 
        }; 
      }; 
      };
      [8] = {
      ["NAME"] = "Bêkhmol"; 
      ["PROGRESS"] = "Find Bêkhmol"; 
      ["LORE"] = "Pirates loyal to neither the fallen Heirs of Castamir nor the reigning Kindred of the Coins attack ships on the sea north of the Cape of Umbar and bring their spoils ashore to Bêkhmol, the Free Wharf."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "14.24S"; 
          ["LONG"] = "100.39W"; 
        }; 
      }; 
      };
      [9] = {
      ["NAME"] = "Idwadâr"; 
      ["PROGRESS"] = "Find Idwadâr"; 
      ["LORE"] = "Idwadâr, the Writhen-home, in the foothills outside of Umbar Baharbêl is home to the rock worms of Umbar. These beasts protect their nest with great ferocity and are a natural enemy of the people."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "15.22S"; 
          ["LONG"] = "101.45W"; 
        }; 
      }; 
      };
      [10] = {
      ["NAME"] = "Taj-kâta"; 
      ["PROGRESS"] = "Find Taj-kâta"; 
      ["LORE"] = "Bog-lurkers covered in reeds make Taj-kâta, the Mire-flats, their ancestral home. Here, the creatures lived in peace for centuries. Now, hunters scour the Mire-flats to acquire seeds and legs from the bog-lurkers to supply the cooks of Umbar Baharbêl."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "14.29S"; 
          ["LONG"] = "97.68W"; 
        }; 
      }; 
      };
      [11] = {
      ["NAME"] = "Jêbel Lakha"; 
      ["PROGRESS"] = "Find Jêbel Lakha"; 
      ["LORE"] = "Jêbel Lakha, the Gathering-stone, is home to two warring prides of lions led by the brothers Skûr and Kabûro. Both beasts have long attacked foolish explorers who wander too close to the territory. The brothers stalk the savannah and stones of Jêbel Lakha in pursuit of one another."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "15.49S"; 
          ["LONG"] = "92.87W"; 
        }; 
      }; 
      };
      [12] = {
      ["NAME"] = "Hâtaphor"; 
      ["PROGRESS"] = "Find Hâtaphor"; 
      ["LORE"] = "Hâtaphor, the Snatcher's Den, is a dangerous area east of Umbar Baharbêl home to a particularly dangerous brood of spiders. The same brood's silk is prized by tailors in the city and often workers are snatched while attempting to recover the silken webbings in the den."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "19.17S"; 
          ["LONG"] = "92.78W"; 
        }; 
      }; 
      };
      [13] = {
      ["NAME"] = "Bâlakhat"; 
      ["PROGRESS"] = "Find Bâlakhat"; 
      ["LORE"] = "Travellers finding themselves in Bâlakhat, the Devourer's Waters, often do not return. The fearsome crocodiles making this area their home are known for their penchant for flesh."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "22.52S"; 
          ["LONG"] = "92.76W"; 
        }; 
      }; 
      };
  };
