      [1] = {
      ["NAME"] = "Jax Phanâl"; 
      ["PROGRESS"] = "Find Jax Phanâl"; 
      ["LORE"] = "Jax Phanâl is a picturesque outpost on the northern tip of the Cape of Umbar. Overlooking the sea, the outpost is home to many citizens reveling in the newfound freedom under the rule of the Kindred of the Coins."; 
