      [2] = {
      ["NAME"] = "Rakhatâb"; 
      ["PROGRESS"] = "Find Rakhatâb"; 
      ["LORE"] = "Rakhatâb is the largest settlement on the Cape of Umbar. Here, workers toil day and night to keep the forge fires burning for the Kindred of the Coins. The fall of the Heirs of Castamir gave hope to the workers, but the transition to the Kindred of the Coins' leadership is fraught with difficulties."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "25.81S"; 
          ["LONG"] = "115.52W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Jid Phêsi"; 
      ["PROGRESS"] = "Find Jid Phêsi"; 
      ["LORE"] = "Jid Phêsi is the Cavern of Whispers, located in the hills north of Rakhatâb. Miners venture into the depths in search of rich ore, gems, and other riches to fuel the industry of the outpost."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "24.38S"; 
          ["LONG"] = "114.76W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Khûtra"; 
      ["PROGRESS"] = "Find Khûtra"; 
      ["LORE"] = "Khûtra is a fishing village on the bay in the Cape of Umbar. The village is recently plagued by an illness claiming livestock and men. The villagers seek a cure for this malady so they can return to their normal lives."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "19.02S"; 
          ["LONG"] = "108.07W"; 
        }; 
      }; 
      };
  };
