  -- Umbar Baharbêl / Umbar Baharbêl, the City of the Corsairs
  [1879477633] = {
      [1] = {
      ["NAME"] = "Calatirion / Beacon-tower of Elendil"; 
      ["PROGRESS"] = "Find Calatirion, the Beacon-tower"; 
      ["LORE"] = "You have discovered Calatirion, the Beacon-tower, which was raised by King Elendil to celebrate his victory over Sauron. However, the tower would not stand forever, for the Dark Lord's servants were not gone, and they would return in a later age. At the command of the Forsaken Reaver, one of the Nazgûl, the Calatirion was toppled. The day of its downfall came to be called the Death of Light, and the horror of it is remembered still by the oldest residents of the city."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "19.88S"; 
          ["LONG"] = "103.85W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Wharf-beacon"; 
      ["PROGRESS"] = "Find the Wharf-beacon"; 
      ["LORE"] = "You have discovered the Wharf-beacon, which welcomes ships arriving to the Sail-haven from long voyages away at sea, guiding them to safety when they arrive at night."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "20.00S"; 
          ["LONG"] = "100.87W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Northern Ship-yard"; 
      ["PROGRESS"] = "Find the Northern Ship-yard"; 
      ["LORE"] = "In the Umbari tongue, the locations where stand the great bones of ships under construction are called Jâsh-bithi. This is the northernmost of the two largest, and it is overseen by Ahûd, who has worked among the ships of the Sail-haven for many years."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "18.84S"; 
          ["LONG"] = "100.15W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Southern Ship-yard"; 
      ["PROGRESS"] = "Find the Southern Ship-yard"; 
      ["LORE"] = "In the Umbari tongue, the locations where stand the great bones of ships under construction are called Jâsh-bithi. This is the southernmost of the two largest, and it is overseen by Râmax, who has sworn to see the Heirsworn who remain in the Sail-haven defeated at last."; 
