  -- Classe / Flèches dans les ténèbres
  [1879052485] = {
      [1] = {
        "Obtenez Flèches dans les ténèbres";
      };
      [2] = {
        "Parlez à Legolas à Fondcombe";
      };
      [3] = {
        "Un Tir dans les ténèbres, page 5 - Cette page contient des entrées relatant les premiers jours du chasseur dans cette région. Il manquait d'entraînement et se retrouvait souvent dans des situations désespérées dont il se sortait in extremis.";
      };
      [4] = {
        "Un Tir dans les ténèbres, page 10 - Ce passage explique comment le chasseur a appris à se déplacer de façon presque aussi silencieuse qu'un Elfe... mais même s'il parvenait à éviter le danger, il ne savait pas encore se servir de son arc sans faire de bruit. Cela suffisait souvent à trahir sa présence.";
      };
      [5] = {
        "Un Tir dans les ténèbres, page 14 - Ce passage ne raconte pas grand-chose d'intéressant. Il raconte comment le chasseur a tenté de poursuivre un sanglier solitaire rencontré dans la forêt.";
      };
      [6] = {
        "Un Tir dans les ténèbres, page 16 - Cette page raconte comment l'auteur s'est retrouvé à la merci d'une bande de gobelins après avoir tenté en vain de traquer un sanglier sauvage. Le seul élément positif de cette aventure fut la détermination du chasseur à s'entraîner et à mieux maîtriser ses compétences.";
      };
      [7] = {
        "Un Tir dans les ténèbres, page 19 - Cette page décrit l'entraînement auquel s'est soumis le chasseur au cœur de la Forêt Noire, à l'est des Monts Brumeux. Le jour où il s'est senti prêt, il est retourné dans les forêts d'Eriador afin de pourchasser les gobelins qui l'avaient forcé à fuir.";
      };
      [8] = {
        "Un Tir dans les ténèbres, page 22 - Cette page décrit la première rencontre du chasseur avec les gobelins après son retour en Eriador. Il découvrit la trace d'un groupe d'éclaireurs gobelins et les suivit jusqu'à leur campement. L'histoire se termine là.";
      };
      [9] = {
        "Un Tir dans les ténèbres, page 23 - Cette page raconte comment le chasseur, grâce à ses nouvelles compétences, réussit à éliminer tout un campement de gobelins sans jamais trahir sa présence. Ses talents d'archer et son aptitude à rester dans l'ombre firent qu'il devint célèbre auprès des ménestrels elfes.";
      };
      [10] = {
        "Un Tir dans les ténèbres, page 25 - La dernière entrée du journal semble décrire la façon dont le chasseur s'est entraîné afin de maîtriser l'art de la peur et de la déstabilisation. L'entrée n'est pas terminée, mais elle pourrait être utile à un bon chasseur.";
      };
  };
  -- Classe / Livre des chasseurs
  [1879052486] = {
      [1] = {
        "Obtenez Livre des chasseurs";
      };
      [2] = {
        "Parlez à Legolas à Fondcombe";
      };
      [3] = {
        "Livre des chasseurs, page 1 - Il s'agit de la page de titre du Livre des chasseurs. Elle confirme que le texte a été traduit par un maître du savoir de Fondcombe.";
      };
      [4] = {
        "Livre des chasseurs, page 3 - Cette page de la préface décrit l'objectif du livre, qui est d'aider ceux qui choisissent de devenir chasseurs à optimiser leurs compétences et leur efficacité, mais sans plus de détails.";
      };
      [5] = {
        "Livre des chasseurs, page 4 - Par une habile transition, l'auteur passe ensuite à l'art de la traque et la survie. Il y a là beaucoup de points qui pourraient servir à un aspirant chasseur.";
      };
      [6] = {
        "Livre des chasseurs, page 8 - Cette page traite de l'utilisation d'un arc à partir d'une perche. Un équilibre précaire peut être à l'origine de beaucoup de tirs ratés.";
      };
      [7] = {
        "Livre des chasseurs, page 17 - Cette page traite de l'utilisation en mouvement d'un arc. Une flèche atteint rarement sa cible lorsqu'elle est tirée en mouvement, même par un Elfe, mais ces techniques peuvent permettre de limiter les problèmes.";
      };
      [8] = {
        "Livre des chasseurs, page 20 - Cette page décrit l'organisation des éclaireurs de Gondolin avant les guerres contre les gobelins. Leurs connaissances tactiques pourraient servir à un chasseur intelligent, mais pas sans les pages manquantes.";
      };
      [9] = {
        "Livre des chasseurs, page 21 - Cette page explique les informations données dans les notes précédentes, et a peu de signification sans l'intégralité des notes préalables.";
      };
      [10] = {
        "Livre des chasseurs, page 28 - Cette page conclut le Livre des chasseurs en résumant ses objectifs, mais ne dit pas grand-chose du contenu en lui-même. C'est une page inutile si l'on ne dispose pas du livre lui-même.";
      };
  };
  -- Classe / La Charge lointaine
  [1879052487] = {
      [1] = {
        "Obtenez Charge lointaine";
      };
      [2] = {
        "Parlez à Legolas à Fondcombe";
      };
      [3] = {
        "La Charge lointaine, page 7 - Cette page est écrite de la main d'un homme aussi à l'aise à la rédaction qu'à la guerre. Elle décrit l'ordre de bataille du régiment dans lequel il se trouvait lors de la Guerre de la Dernière Alliance.";
      };
      [4] = {
        "La Charge lointaine, page 9 - Cette page se trouve au début d'une section du livre décrivant les premières phases de la guerre, avant que l'alliance entre les Hommes et les Elfes ne soit solidement fondée et alors que la suspicion et le doute existaient des deux côtés... au moment où la véritable menace constituée par le Mordor commençait à apparaître. Ce régiment se trouvait depuis longtemps posté au Nord pour garder l'Anduin à son entrée dans le royaume du Gondor. C'est là que les stratagèmes de l'Ennemi le forcèrent à affronter une expédition de guerriers elfes venant de la Lórien, au Sud.";
      };
      [5] = {
        "La Charge lointaine, page 10 - Cette page décrit les stratagèmes utilisés par l'Ennemi pour déclencher la terrible bataille entre les forces du Gondor et de la Lórien. A l'époque, les forces du Gondor comptaient des marginaux qu'ils payaient pour qu'ils leur rapportent des nouvelles des terres lointaines, comme par exemple les Marches Orientales du Rohan et les Terres Sauvages. Ils avaient oublié quelque part qu'il ne fallait pas sous-estimer la capacité de l'Ennemi à corrompre le cœur des Hommes... Ces éclaireurs indignes de confiance leur parlèrent d'une force étrangère descendant la rivière du Nord, force armée et revêtue d'armures de guerre et non annoncée, car les hérauts avaient été assassinés par des ombres ténébreuses à l'insu des deux armées.";
      };
      [6] = {
        "La Charge lointaine, page 14 - La bataille commença par un matin brumeux, les vaillants Hommes du Gondor attaquèrent la première ligne de l'armée des soi-disant envahisseurs, semant la confusion et la panique chez les Elfes qui ne se doutaient de rien. Mais le régiment fut doublement trahi, car les collaborateurs de l'Ennemi avaient également menti sur la taille de l'armée elfe descendant du Nord. Lorsque la majeure partie de l'armée arriva au sommet d'une colline avoisinante, les Hommes du Gondor furent atterrés en voyant que l'armée faisait deux fois la taille annoncée et était quasiment égale à la leur.";
      };
      [7] = {
        "La Charge lointaine, page 17 - Pensant que le pire était arrivé, c'est-à-dire que les Elfes s'étaient ligués avec l'Ennemi et venaient semer la défaite et la ruine chez les Hommes du Gondor, ceux-ci continuèrent à se battre de plus belle, de peur que tout espoir de victoire disparaisse dans un moment d'hésitation. Leurs capitaines rallièrent les hommes contre le reste de la première ligne des Elfes et leur demandèrent de se préparer à attaquer les Elfes, dans l'espoir de les repousser avec férocité, sachant que les prouesses et l'armure de leurs ennemis immortels leur assureraient la victoire si le cours du combat changeait ne serait-ce qu'un instant.";
      };
      [8] = {
        "La Charge lointaine, page 18 - La page donne ensuite une description du concert de trompettes et de la marche des étendards ce jour-là, alors que la brume se levait et que le soleil montait dans le ciel lorsque le régiment se lança à l'assaut de la colline. Le capitaine décrit ensuite sa propre stupéfaction lorsqu'il vit les archers de l'armée des Elfes se préparer à contre-attaquer, lançant leurs flèches vers le ciel... Les mouvements des archers semblaient presque paresseux, mais ils procédaient tous avec précision et rapidité, contredisant ainsi leur apparente indifférence.";
      };
      [9] = {
        "La Charge lointaine, page 21 - Cette page décrit le chaos qui s'ensuivit lorsque les hommes et les chevaux effrayés se retrouvèrent sous une pluie de flèches mordant les armures, les hommes et le sol avec une indifférence cruelle. Le capitaine explique que ce ne fut pas la précision mortelle des flèches qui provoqua la terreur et le chaos, mais leur côté intense et aléatoire... aucun homme ne pouvait se sentir en sécurité ou protégé à aucun moment, qu'il porte un bouclier ou marche dans l'ombre d'un autre. Beaucoup s'arrêtèrent, cherchant en vain à se mettre à l'abri. La pluie de flèches continua jusqu'à ce que le chaos et le désordre règnent en maîtres, et les quelques âmes vaillantes qui réussirent à atteindre l'armée des Elfes périrent sous le coup d'épées et de piques maniées avec une efficacité impitoyable.";
      };
      [10] = {
        "La Charge lointaine, page 24 - La dernière entrée du capitaine décrit les événements qui suivirent la bataille, lorsque les Elfes les firent prisonniers, lui et quelques autres survivants, et découvrirent le stratagème de l'Ennemi... qui n'avait pas prévu que la bataille trouverait une issue aussi décisive et qui avait espéré que les Hommes de l'Ouest remportent la victoire sur les forces de Lórien afin de porter un coup à l'alliance naissante. Mais les Elfes, ayant connu peu de pertes, décidèrent alors de diriger toute leur colère vers Barad-dûr. Le capitaine termine en considérant l'ironie de la défaite de son régiment. Si son régiment avait agi différemment sur le champ de bataille ce jour-là... si l'issue du combat avait été différente, que serait-il advenu du Royaume du Gondor et de la Terre du Milieu par la suite ? Maigre réconfort pour toutes ces pertes, mais ce sont là les caprices du destin et de la guerre.";
      };
  };
  -- Classe / La Flamme de la bougie
  [1879053012] = {
      [1] = {
        "Obtenez Flamme de la bougie";
      };
      [2] = {
        "Parlez à Boromir à Fondcombe";
      };
      [3] = {
        "La Flamme de la bougie, page 4 - Cette page relate la vie de l'auteur, capitaine au service d'Elendil, le Haut Roi de toute la Terre du Milieu. L'auteur a survécu à l'attaque d'Elendil contre Barad-dûr et est devenu un chef respecté dans les armées d'Isildur.";
      };
      [4] = {
        "La Flamme de la bougie, page 7 - Cette page résume brièvement le point principal du traité de l'auteur : la valeur d'un capitaine se reconnaît à la loyauté et la dévotion des hommes qui le suivent.";
      };
      [5] = {
        "La Flamme de la bougie, page 13 - Sur cette page, l'auteur relate ses expériences en tant qu'homme d'armes novice à Numénor, sous le commandement d'un capitaine cruel dans le palais d'Ar-Pharazon. Ce passage indique clairement ce qu'un véritable capitaine n'est pas.";
      };
      [6] = {
        "La Flamme de la bougie, page 16 - Cette page décrit un peu plus la montée de l'auteur parmi les rangs de la Garde de Numénor, lieutenant au service d'un capitaine qui attendait de ses subordonnés qu'ils gèrent les rangs. Il s'agit là d'un autre exemple de mauvais commandement pouvant briser la motivation des hommes qui se battent.";
      };
      [7] = {
        "La Flamme de la bougie, page 25 - Cette page raconte comment il fut introduit dans le foyer d'Elendil, dernier chef des Fidèles, pour l'honneur et la dévotion que lui montraient les hommes sous son commandement, par amour pour un grand capitaine, et non par peur ou par obligation.";
      };
      [8] = {
        "La Flamme de la bougie, page 26 - Cette page relate le moment où l'auteur a accepté son nouveau poste et son propre engagement envers le plus grand de tous les capitaines mortels, Elendil de Numénor.";
      };
      [9] = {
        "La Flamme de la bougie, page 31 - Sur cette page, l'auteur décrit sa volonté de se battre et de mourir pour son roi, et également la dévotion de ses hommes envers lui, signe de l'excellence de son commandement.";
      };
      [10] = {
        "La Flamme de la bougie, page 35 - Cette page raconte que si un capitaine se montre suffisamment grand pour commander la loyauté sans faille des ses hommes, il est parfois possible que la voix du capitaine ramène à la vie ceux qui se trouvent au seuil de la mort. Il y a d'autres choses écrites, mais je n'arrive pas à les déchiffrer, il y a trop de taches.";
      };
  };
  -- Classe / Le Traité du courage
  [1879053013] = {
      [1] = {
        "Obtenez Traité du courage";
      };
      [2] = {
        "Parlez à Boromir à Fondcombe";
      };
      [3] = {
        "Traité du courage, page 2 - Cette page fait partie de l'introduction et parle des auteurs. Le conseil ayant écrit le livre était composé de quelques-uns des plus grands esprits tactiques d'Arnor, et leurs compétences en matière de tactiques défensives sauvèrent beaucoup de vie, même si cela ne suffit pas à la fin.";
      };
      [4] = {
        "Traité du courage, page 3 - La première section fut rédigée par un capitaine expert en coups de poings. Il décrit diverses méthodes permettant de se défendre lorsque l'on n'est pas armé.";
      };
      [5] = {
        "Traité du courage, page 5 - Cette section fut rédigée par un capitaine connu pour ses compétences en matière de combat armé. Ses techniques de défense armée pourraient encore servir aux plus grands épéistes actuels.";
      };
      [6] = {
        "Traité du courage, page 10 - Ce chapitre fut écrit par un capitaine préoccupé par la menace constituée par le feu et l'huile bouillante lors des sièges. Il met l'accent sur l'utilisation de murs de boucliers et d'autres stratégies pour résister au feu.";
      };
      [7] = {
        "Traité du courage, page 12 - Cette section fut rédigée par un capitaine spécialisé dans les tactiques par temps rude. Ses connaissances en matière de protection contre les grands froids et la pluie peuvent permettre de garder les soldats en vie sur le champ de bataille.";
      };
      [8] = {
        "Traité du courage, page 15 - Cette page fut rédigée par un capitaine spécialisé dans les armes des Orques et des gobelins, et particulièrement dans les poisons utilisés par ces créatures. Tout guerrier souhaitant apprendre à se défendre contre les lames empoisonnées devrait lire ce chapitre dans son intégralité.";
      };
      [9] = {
        "Traité du courage, page 18 - Ce chapitre fut rédigé par Isildur, le roi d'Arnor. Il traite de magie noire et de la façon dont cette magie détruit l'esprit. Le roi explique comment se protéger contre ses ravages et parle d'expérience, puisqu'il affronta lui-même Sauron, le Seigneur Ténébreux, et en sortit vainqueur.";
      };
      [10] = {
        "Traité du courage, page 22 - Cette dernière page résume tout ce qui a été dit auparavant, et recommande au lecteur de se souvenir avant tout que les plus grands capitaines ne mentent pas à ceux qui les servent et sont fidèles à leurs seigneurs.";
      };
  };
  -- Classe / Livre des serments
  [1879053014] = {
      [1] = {
        "Obtenez Livre des serments";
      };
      [2] = {
        "Parlez à Boromir à Fondcombe";
      };
      [3] = {
        "Livre des serments, page 6 - Cette page relate une discussion poussée concernant le caractère grave des serments et débat du côté opportun ou non des serments accordés par des personnes de relations ou rangs différents. Elle met particulièrement l'accent sur le danger inhérent au fait qu'une personne de situation supérieure accorde un serment à une autre de situation inférieure, car dans ce cas le serment lie non seulement la personne ayant prêté serment, mais tous ses vassaux et sujets au sort de celui qui prête serment, pour le meilleur et pour le pire.";
      };
      [4] = {
        "Livre des serments, page 8 - Cette section du livre relate une discussion enflammée entre plusieurs philosophes sur la nature des serments et le destin de ceux qui sont liés par un serment. D'un côté, ceux qui pensent que les paroles et l'intention de la personne qui prête un serment servent à façonner le destin lui-même, puisqu'elles les lient de façon irrévocable et que ce lien peut guider les actions de ceux qui y sont associés... une sorte d'épée de Damoclès au-dessus de la personne ayant prêté serment, qui subirait un sort terrible si celui-ci était brisé. De l'autre côté, les érudits qui rejettent la notion que de simples mots puissent façonner le monde, insistant plutôt sur le fait que ce sont les actions de la personne qui a prêté serment qui décident de son sort. Ils pensent que le serment lui-même a uniquement pour but de focaliser la volonté ou de prouver la trahison de celui qui le prête, et que la honte de l'échec, la corruption de l'esprit et le désappointement de ses pairs constituent un sort suffisamment terrible pour le briseur de serment, sans qu'aucune intervention divine ne soit requise";
      };
      [5] = {
        "Livre des serments, page 9 - Cette page mène à une discussion en profondeur sur le sort connu par un ancien Elfe qui était un Seigneur parmi les Seigneurs, et qui a prêté un serment si puissant et terrible qu'il aurait changé le cours de l'histoire pendant plusieurs siècles, et le sort d'innombrables Elfes, Hommes et Nains. Malheureusement, la discussion demeure assez abstraite, étant donné que les érudits ayant rédigé ce livre ne semblaient disposer que de légendes et de mythes à ce sujet, et les Elfes de l'époque ne voulaient pas aborder ce sujet. Néanmoins, si les événements décrits par les érudits ont un fond de vérité, cela suggère que le serment prêté par ce seigneur elfe pourrait avoir été le serment le plus puissant jamais prêté en Terre du Milieu par quiconque, mortel ou immortel, sorcier ou roi.";
      };
      [6] = {
        "Livre des serments, page 14 - Cette page relate une discussion hautement ésotérique concernant les serments et les dragons. Les véritables dragons sont connus pour leur capacité à altérer les mots dans leur propre intérêt et à confondre l'esprit de ceux qui sont suffisamment bêtes pour traiter avec eux. L'auteur tente de déterminer si une créature telle qu'un dragon pourrait véritablement être liée par un serment, quelle que soit la façon dont il lui ait été accordé, ou si le dragon se servirait de sa maîtrise des mensonges et de la duperie pour créer un serment tel que la personne à qui il serait accordé se retrouverait liée à leur place. Cette discussion, bien qu'intéressante, tourne un peu en rond, car il devient vite évident que l'auteur n'a pas vraiment eu l'occasion de parler à des dragons et a eu la présence d'esprit de ne pas se lancer dans une expérience qui aurait pu appuyer ses théories....";
      };
      [7] = {
        "Livre des serments, page 17 - Cette page traite de l'importance avec laquelle tous les serments doivent être considérés, même les plus anodins. Cette section insiste sur le fait que même si tous les serments lient les personnes qui les prêtent, la force de ce lien et le sort qui s'ensuit n'ont pas grand-chose à voir avec la gravité des paroles prononcées, et beaucoup plus avec les événements imprévus qui s'ensuivent. L'érudit mentionne à ce sujet l'histoire d'un serment simple et amical offert à l'anniversaire d'un noble des maisons de Numénor, serment qui finit par traîner sa maison dans une guerre interminable qui détruisit la fortune de sa famille. Le pauvre bougre mourut sans un sou. Malheureusement, il est impossible de savoir si cette histoire est vraie ou s'il s'agit plutôt d'une légende inventée par l'auteur pour étayer son argument.";
      };
      [8] = {
        "Livre des serments, page 19 - Il existe de nombreuses histoires concernant les conséquences terribles d'un serment prêté ou brisé bêtement. Cette page relate une discussion concernant la force pouvant être gagnée grâce à de tels liens, lorsqu'ils sont établis de façon réfléchie et respectés. Elle raconte une vieille légende selon laquelle un Homme et une jeune Elfe étaient liés par un serment d'amour si puissant que même lorsqu'ils se retrouvèrent piégés par le plus grand Ennemi de l'époque, ils réussirent à lui échapper et à retrouver la lumière du soleil, en emportant son plus grand trésor avec eux.";
      };
      [9] = {
        "Livre des serments, page 23 - Cette page décrit en détail le serment du roi de Numénor, que ce dernier prêtait en montant sur le trône. Elle raconte avec moult détails comment ce serment a changé le cours de plusieurs dynasties, et toutes les ramifications légales et rituelles de ces changements. En dépit de la masse d'informations historiques contenues dans le livre, le style ampoulé et les détails excessivement complexes commencent à vous donner mal à la tête alors que vous avez à peine lu quelques-uns des paragraphes écrits avec minutie. Vous vous dites que vous risquez de vous endormir ou de perdre la raison si vous continuez à lire.";
      };
      [10] = {
        "Livre des serments, page 28 - Cette page se détache des autres. L'écriture est désordonnée et difficile à lire, et traite d'un sujet troublant, très différent de ceux abordés dans le reste du livre. L'auteur de cette section particulière semblait obsédé par les esprits restant dans le monde des vivants bien après la mort de l'enveloppe corporelle, et des causes possibles de cette non-mort étrange et troublante. Dans certains cas, ces esprits auraient revendiqué un serment non résolu ou brisé comme lien les maintenant dans ce monde, et l'auteur aborde ce sujet avec une grande curiosité, se demandant quel pouvoir pourrait rendre le lien d'un serment prêté par un homme si fort qu'il le forcerait à rester dans ce monde après sa mort. Les passages suivants sont encore plus troublants, car il devient clair que la fascination de l'auteur à ce sujet est devenue malsaine, alors qu'il se demande si des serviteurs et des esclaves pourraient éventuellement être poussés à prêter des serments qui les lieraient à leurs seigneurs après leur mort. Le passage ne mentionne pas si l'auteur de cette section a trouvé réponse à cette question, ce qui vous soulage grandement";
      };
  };
  -- Classe / Le Livre des épées
  [1879059839] = {
      [1] = {
        "Obtenez Livre des épées";
      };
      [2] = {
        "Allez voir Gimli à Fondcombe";
      };
      [3] = {
        "Livre des épées, page 1 - Il s'agit de la page d'ouverture du Livre des épées. Elle contient un titre et une dédicace : En mémoire du Beleriand, englouti par la mer.";
      };
      [4] = {
        "Livre des épées, page 4 - Cette page raconte l'histoire de héros elfes des temps anciens, des héros ayant combattu lors des guerres contre les gobelins, défendant Gondolin contre les armées d'Orques qui saccageaient tout sur leur passage.";
      };
      [5] = {
        "Livre des épées, page 7 - Les seigneurs elfes ayant rédigé ce tome ont pris le temps d'y inscrire quelques ballades à la gloire de ceux qui sont morts depuis longtemps, à Beleriand et dans la Terre du Milieu. Peut-être qu'ils espéraient que ces vieilles chansons encourageraient le lecteur à faire le bien.";
      };
      [6] = {
        "Livre des épées, page 12 - Cette page exhorte le lecteur à ne jamais avoir peur et à ne jamais reculer devant le mal.";
      };
      [7] = {
        "Livre des épées, page 13 - Cette page parle de courage face aux créatures les plus maléfiques. Le véritable champion se trouvera toujours au cœur du combat, quelles que soient ses chances de victoire.";
      };
      [8] = {
        "Livre des épées, page 17 - C'est à partir de cette page que commence l'enseignement en matière de guerre armée. Elle contient une liste des techniques communément utilisées par les grands guerriers du passé.";
      };
      [9] = {
        "Livre des épées, page 21 - Cette page contient principalement des diagrammes détaillés et des portraits des maîtres elfes. Les diagrammes ne sont pas faciles à comprendre étant donné que le tome n'est pas complet.";
      };
      [10] = {
        "Livre des épées, page 25 - Cette page résume le Livre des épées et relie ses points les plus importants.";
      };
  };
  -- Classe / La Beauté du combat
  [1879059840] = {
      [1] = {
        "Obtenez Beauté du combat";
      };
      [2] = {
        "Allez voir Gimli à Fondcombe";
      };
      [3] = {
        "La Beauté du combat, page 11 - Cette page, qui se trouve dans le premier tiers de la Beauté du combat, commence au milieu du récit d'une grande bataille entre les nains de Khazad-dûm et les gobelins des Monts Brumeux. Parmi les récits de ce combat sont relatés quelques-uns des propres actes de courage de l'auteur.";
      };
      [4] = {
        "La Beauté du combat, page 14 - Cette page décrit la rencontre de l'auteur avec le chef des envahisseurs gobelins. Il se battait depuis des heures et commençait à fatiguer, mais il réussit à rassembler suffisamment de force pour défendre ses camarades.";
      };
      [5] = {
        "La Beauté du combat, page 18 - Dans ce texte, l'auteur exhorte le lecteur à ne pas se laisser emporter dans la bataille au point d'en oublier de voir les dangers encourus par ses alliés, mais plutôt de permettre à l'excitation et l'énergie générées par le combat de renforcer ses bras afin d'affronter toutes les menaces pesant sur lui ou ses alliés.";
      };
      [6] = {
        "La Beauté du combat, page 24 - A cette page commence une série de méditations réalisées par l'auteur lors de sa préparation au combat. Il s'agit en fait plutôt de cris de ralliement et de motivations que de méditations profondes... dont il se servait pour se mettre dans l'état d'esprit du combat.";
      };
      [7] = {
        "La Beauté du combat, page 25 - Les méditations de l'auteur continuent sur cette page, mais son écriture commence à devenir moins distincte, comme s'il se retrouvait pris dans sa propre diatribe.";
      };
      [8] = {
        "La Beauté du combat, page 28 - Cette page ressemble à un discours joyeux et assoiffé de sang. Soit l'auteur avait perdu sa motivation, soit il voulait faire battre le cœur du lecteur.";
      };
      [9] = {
        "La Beauté du combat, page 32 - Le ton de cette page est bien plus calme que celui des précédentes. L'auteur explique l'importance de la détermination et de l'idéalisme pour la force intérieure du champion.";
      };
      [10] = {
        "La Beauté du combat, page 36 - Cette page reprend tout ce qui a été dit auparavant. Certaines pages sont illisibles, mais toute personne lisant ce livre pourrait bénéficier des enseignements et de l'enthousiasme de l'auteur.";
      };
  };
  -- Classe / Vous avez découvert La Lame de l'artisan
  [1879059841] = {
      [1] = {
        "Obtenez Lame de l'artisan";
      };
      [2] = {
        "Allez voir Gimli à Fondcombe";
      };
      [3] = {
        "La Lame de l'artisan, page 2 - Les premières sections du livre semblent concentrées sur l'importance des exercices de base, à la manière de n'importe quel guide de guerrier, mais l'auteur approfondit les choses et suggère l'utilisation de nouvelles techniques de méditation avant et après les exercices afin d'en améliorer l'efficacité.";
      };
      [4] = {
        "La Lame de l'artisan, page 4 - Cette page explique l'art de la guérison, et non pas l'art du combat. L'auteur incite à la prudence. Ces exercices rigoureux peuvent blesser le guerrier avant même son entrée sur le champ de bataille, et recommande un traitement spécial à base de compresses aux herbes et de vêtements chauds pour aider les muscles à guérir plus rapidement après cet entraînement éreintant.";
      };
      [5] = {
        "La Lame de l'artisan, page 6 - Vous n'aviez jamais vu une telle attention portée aux détails dans un traité pour les guerriers. L'auteur a été jusqu'à mesurer la force avec laquelle un coup devait être porté pour fendre l'armure d'un ennemi, qu'elle soit en cuir, en mailles ou en acier des nains trempé. Il explique ensuite comme un guerrier peut procéder pour évaluer la force de ses coups afin que ceux-ci portent à chaque fois, avec une incroyable économie de mouvements et d'énergie.";
      };
      [6] = {
        "La Lame de l'artisan, page 9 - Cette page est couverte de schémas du bras, illustrant diverses positions de l'épaule, du coude et du poignet, ainsi que leurs amplitudes de mouvement. Il semble explorer les diverses façons dont une arme peut être maniée afin d'obtenir des effets optimaux.";
      };
      [7] = {
        "La Lame de l'artisan, page 14 - Cette page répertorie une série de poids pouvant être attachés à une épée ou une masse afin d'en augmenter le poids et de renforcer le bras lors des exercices. L'auteur suggère une série d'augmentations lente mais régulière, jusqu'au moment où le poids de l'arme semble impossible à manier pour quiconque n'est pas un troll. Vous vous demandez si l'auteur est sérieux, mais le reste du livre est si logique et si méthodique que vous vous dites qu'il doit l'être. On pourrait en revanche se demander combien de ses élèves ont survécu à ce régime.";
      };
      [8] = {
        "La Lame de l'artisan, page 15 - Cette page décrit la zone d'entraînement que l'auteur a construite. Elle paraît grande et spartiate, se limitant aux nécessités de l'art du combat et ignorant tous les conforts ou distractions possibles. Vous vous dites que ce type ne devait pas être du genre à faire souvent la fête ou à avoir beaucoup d'amis.";
      };
      [9] = {
        "La Lame de l'artisan, page 17 - Cette page est la première à traiter de la technique au-delà des mouvements les plus basiques. Elle explique comment l'élève – ayant normalement suivi tout ce qui était indiqué auparavant – doit à présent apprendre à conserver la vitesse et la puissance de ses coups, qu'ils soient portés sur du bois, du métal, de la chair ou de l'air alors que son adversaire se déplace et esquive. Le guerrier capable de garder son arme en mouvement en permanence se fatiguera moins vite et gardera l'avantage en forçant son adversaire à réagir en continu et non coup par coup.";
      };
      [10] = {
        "La Lame de l'artisan, page 26 - L'auteur donne un résumé de l'entraînement et des exercices traités dans le livre, les reliant les uns aux autres de façon claire et nette, comme tout le reste. Vous vous surprenez à vous dire qu'un enfant pourrait apprendre à se battre en maître s'il suivait ces méthodes... enfin, si l'enfant en question avait du sang de troll dans les veines et une dévotion fanatique au combat à l'épée, que beaucoup considéreraient comme une obsession malsaine....";
      };
  };
  -- Classe / La Meilleure défense
  [1879060151] = {
      [1] = {
        "Obtenez Meilleure défense";
      };
      [2] = {
        "Allez voir Samwise Gamegie à Fondcombe";
      };
      [3] = {
        "La Meilleure défense, page 2 - Cette page, qui fait partie de l'introduction, révèle que l'auteur était le chef des gardes de Thrain I, ancêtre du Grand Thorin Ecu de Chêne et premier roi sous la montagne.";
      };
      [4] = {
        "La Meilleure défense, page 3 - Cette page établit les prémices du livre par un cliché terriblement commun : 'La meilleure défense, c'est l'attaque.'";
      };
      [5] = {
        "La Meilleure défense, page 5 - Cette page décrit quelques-unes des postures les plus rudimentaires qu'un gardien peut utiliser pour tirer parti des erreurs commises par ses ennemis.";
      };
      [6] = {
        "La Meilleure défense, page 9 - A cette page, l'auteur continue de donner des instructions et développe les nuances du combat permettant à un gardien de percer une brèche dans les défenses de l'ennemi en contrant ses attaques.";
      };
      [7] = {
        "La Meilleure défense, page 12 - Cette page comprend plusieurs esquisses rudimentaires des postures et mouvements décrits par l'auteur dans les pages précédentes, mais les illustrations sont difficiles à suivre hors contexte.";
      };
      [8] = {
        "La Meilleure défense, page 16 - A cette page, l'auteur commence à couvrir des postures plus avancées permettant à un gardien de parer une attaque, avec ensuite une contre-attaque rapide et puissante. La technique n'est pas si simple qu'il y paraît.";
      };
      [9] = {
        "La Meilleure défense, page 17 - Le dernier diagramme décrit de façon plus détaillée les techniques illustrées dans les pages précédentes. Toutes ces techniques paraissent simples, mais sont bien plus complexes que le lecteur peut l'imaginer.";
      };
      [10] = {
