  -- Monts Brumeux / Les Hauteurs repoussantes
  [1879099039] = {
      [1] = {
      ["NAME"] = "Cirith Daur"; 
      ["LORE"] = "Cirith Daur était autrefois l'un des cols les plus empruntés par les voyageurs pour gagner la région du Haut Col. Même quand le temps est favorable, il est balayé par des vents et des tempêtes et est difficile à traverser. Souvent infranchissable en basse saison, il bloque alors tout accès entre les communautés à l'est et à l'ouest des Monts Brumeux."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "23.28S"; 
          ["LONG"] = "3.47E"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "La Fissure Noire"; 
      ["LORE"] = "Cette petite grotte dans les rochers du Haut Col pourrait servir d'abri aux voyageurs en cas de tempête, si l'on n'avait découvert que c'est une entrée dissimulée menant aux terriers de Gobelinville, d'où les gobelins émergent une fois la nuit tombée pour enlever ceux qui sont assez stupides pour camper dans ce coin.\n\nCes jours-ci, la fissure est peu utilisée, car de moins en moins de personnes empruntent ce col et les gobelins sortent de plus en plus souvent par la porte principale, alors qu'ils se préparent à la guerre."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "20.31S"; 
          ["LONG"] = "5.35E"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Domstoll"; 
      ["LORE"] = "Domstoll - plus souvent nommé le Trône du Géant en westron, est un amas de pierres massives qui surplombe les ruines abandonnées de Starkhath. Ce lieu offre une vue dominante sur la région environnante quand la neige et la brume ne sont pas de la partie.\n\nLa plupart des géants ont quitté cet endroit il y a bien longtemps, mais on raconte qu'il y en aurait au moins un qui surveillerait ce domaine solitaire."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "21.95S"; 
          ["LONG"] = "8.63E"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Crevasse d'Erforgh"; 
      ["LORE"] = "La Crevasse d'Erforgh est un fouillis de rochers et de ruisseaux gelés abandonnés par une grande masse de glace lors des âges précédents. Elle offre désormais un semblant d'abri contre les vents montagnards les plus violents à une tribu nomade de Gauradans qui s'y est installée.\n\nQue les Gauradans soient venus ici de leur propre chef ou qu'ils aient été dirigés par les pouvoirs d'Angmar, leur présence reste néanmoins troublante."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "29.60S"; 
          ["LONG"] = "9.15E"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Serpentines"; 
      ["LORE"] = "Les Serpentines sont un ensemble de grottes formé par une masse imposante de rochers, trop gros pour que même les géants puissent les soulever. Comment ils se sont retrouvés ainsi est une énigme. Peut-être suite à la chute d'un pic sur la montagne ou après un événement de même portée cataclysmique.\n\nLes grottes sont étrangement plus chaudes que les pentes extérieures balayées par le vent. Elles sont peut-être réchauffées par une source chaude loin en dessous de la montagne. Cependant, tout voyageur qui espère y trouver abri devrait chercher ailleurs, car cette même chaleur a attiré une importante colonie de vers mortels dans ces grottes. S'y promener avec insouciance conduit à une mort certaine."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "26.71S"; 
          ["LONG"] = "8.84E"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Starkhath"; 
      ["LORE"] = "Des voyageurs qui sont passés par ici il y a de nombreuses années ont décrit un endroit où les géants jetaient des rochers pour se divertir.\n\nLes géants semblent avoir disparu maintenant. Certains disent qu'ils ont été chassés lorsqu'un nouveau Grand Gobelin a pris le trône de Gobelinville et que les relations entre les deux peuples sont devenues intolérables. Avec le temps, fatigués des tourments qu'ils enduraient, les géants ont quitté cet endroit et se sont dirigés vers Iorbar, au sud-ouest.\n\nSeuls les blocs de pierre qu'ils utilisaient autrefois comme abris sont toujours debout. Ils réverbèrent les hurlements des ouargues qui errent désormais en ce lieu."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "20.95S"; 
          ["LONG"] = "8.02E"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Hrimbarg"; 
      ["LORE"] = "Fondée avant la cité d'Helegrod, Hrimbarg était autrefois nommée 'La Couronne des Monts Brumeux' par les nains qui vivaient là et qui la considéraient comme le lieu de résidence le plus élevé de leur peuple, dans le monde de cette époque.\n\nHélas, elle s'effondra, et sa chute ne fut causée ni par des gobelins ni par des géants et encore moins par des dragons. Mais, comme le raconte l'histoire, par une puissante tempête qui balaya le Haut Col sans faiblir pendant cinq longues années, bloquant les cols avec d'énormes congères de glace, enterrant même Hrimbarg sous un immense manteau de neige.\n\nQuand, enfin, la tempête se calma et que les habitants de la Moria virent du nord pour libérer leurs frères de Hrimbarg, on ne trouva aucun nain en vie. Ils étaient tous morts de froid ou de faim pendant les longues années d'isolement. On décida donc de ne pas recoloniser Hrimbarg. Elle est désormais vide, depuis des temps immémoriaux."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "24.45S"; 
          ["LONG"] = "6.95E"; 
        }; 
      }; 
      };
      [8] = {
      ["NAME"] = "Akûltot"; 
      ["LORE"] = "Construit peu après l'ascension du nouveau Grand Gobelin de Gobelinville, le camp d'Akûltot est installé sur une série de saillies et de pics rocheux qui surplombent l'approche vers les portes principales de Gobelinville. Historiquement, les gobelins des Monts Brumeux ont toujours refusé la construction de camps dans les congères inhospitalières du Haut Col. Il est donc plutôt troublant qu'ils décident de le faire maintenant.\n\nLa seule explication probable est qu'ils se préparent à étendre leur emprise sur les montagnes d'une manière ou d'une autre, ce qui est sûrement une mauvaise nouvelle pour ceux qui vivent dans les collines et les forêts en contrebas."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "21.50S"; 
          ["LONG"] = "4.51E"; 
        }; 
      }; 
      };
  };
  -- Monts Brumeux / Gobelinville
  [1879099040] = {
      [1] = {
      ["NAME"] = "Fort de guerre"; 
      ["LORE"] = "Le fort de guerre est une immense caserne de Gobelinville, où soldats et francs-tireurs se réunissent et préparent des raids sur les villages de chaque côté des montagnes. Ils descendent pour la plupart via les passages vers l'est pour atteindre les camps des Hommes les plus petits et les moins bien défendus qui bordent la Forêt Noire. Mais, en certaines occasions, quand ils sont suffisamment nombreux, il leur arrive de s'en prendre aux communautés elfes des environs de Fondcombe, même si ces assauts téméraires tournent généralement au désastre pour les gobelins, peu d'entre eux pouvant de vanter d'en revenir vivants."; 
      ["POINTS"] = {
