  [1879141039] = {
      [1] = {
      ["NAME"] = "La Longue Descente"; 
      ["LORE"] = "La Longue Descente est une ancienne salle de garde, qui commandait l’accès aux mines situées en contrebas. Les nains ont réussi à l’investir, ce qui leur permet de surveiller le passage entre les Filons de la Corne d'Argent et la Grande Prospection, et donc de parer à une invasion de gobelins. Demeurant toutefois largement inférieurs en nombre, ils doivent opposer une résistance incessante pour préserver leur mainmise sur cette salle, contre les sauvages ennemis qui occupent les profondeurs."; 
      ["POINTS"] = {
