      [4] = {
      ["NAME"] = "L'ancienne cité de la Corne d'Argent"; 
      ["LORE"] = "La vieille cité de la Corne d'Argent se trouve au sud des filons de la Corne d'Argent et accueillait sans doute les mineurs à la recherche de mithril dans les profondeurs sous la grande montagne."; 
      ["POINTS"] = {
