  -- Race et statut social / Cartographe : Moria
  [1879206033] = {
      [1] = {
        "Obtenez la carte des murs de la Moria - Cette carte représente la zone autour de l'entrée de la Moria, également appelée la Porte de Houssaye.";
      };
      [2] = {
        "Obtenez la carte des Filons de la Corne d'Argent - C'est une carte de la zone de la Moria que les nains appellent les Filons de la Corne d'Argent.";
      };
      [3] = {
        "Obtenez la carte de la Voie de Durin - Cette carte représente le chemin principal de la Moria, également appelé la Voie de Durin.";
      };
      [4] = {
        "Obtenez la carte du système hydraulique - Cette carte du système hydraulique de la Moria montre les anciennes parties aujourd'hui inondées de la grande région du royaume.";
      };
      [5] = {
        "Obtenez la carte de Zelem-melek - Cette carte indique la région de la Moria connue sous le nom de Zelem-melek";
      };
      [6] = {
        "Obtenez une carte de Nud-melek - Cette carte indique les lieux importants des régions connues sous le nom de Nud-melek dans la Moria.";
      };
      [7] = {
        "Obtenez une carte des Fondations de Pierre - Cette carte détaille le lieu connu comme les Fondations de Pierre de la Moria.";
      };
      [8] = {
        "Obtenez une carte des Filons du Rubicorne - Cette carte indique les lieux importants des régions connues sous le nom des Filons du Rubicorne de la Moria.";
      };
      [9] = {
        "Obtenez une carte de Zirakzigil - Cette carte indique un endroit sur le flanc des montagnes hors de la Moria.";
      };
      [10] = {
        "Obtenez une carte des Profondeurs Ardentes - Cette carte indique les lieux importants des régions connues sous le nom des Profondeurs Ardentes de la Moria.";
      };
      [11] = {
        "Obtenez une carte du Grand Escalier - Cette carte détaille un endroit qui était autrefois un carrefour très emprunté de la Moria. Aujourd'hui, c'est la demeure de nombreuses factions de gobelins dans les profondeurs.";
      };
  };
  -- Race et statut social / Cartographe : Eriador
  [1879206037] = {
      [1] = {
        "Obtenez la carte de la Vieille Forêt - La Vieille Forêt est un endroit où le danger règne partout. Vous avez obtenu une copie de la carte de la Vieille Forêt.";
      };
      [2] = {
        "Obtenez la carte d'Archet - Archet, un petit village de Bree qui a récemment été incendié par les brigands du Plateau Noir. Vous avez obtenu une carte de la ville et des environs.";
      };
      [3] = {
        "Obtenez la carte de la ville de Bree - Bree est la ville principale du Pays de Bree. Elle abrite des endroits célèbres tels que l'Auberge du Poney Fringant et la Fontaine aux sangliers. Vous avez trouvé une superbe carte de la ville de Bree.";
      };
      [4] = {
        "Obtenez la carte d'Annuminas - Autrefois demeure des rois des Hommes, Annuminas est aujourd'hui une ville inondée sur la rive sud du lac Evendim dans la région d'Evendim. C'est une carte de la zone.";
      };
      [5] = {
        "Obtenez la carte des Landes d'Etten - Les Landes d'Etten sont une région oubliée du monde, où le mal s'est introduit partout. C'est une carte de la zone.";
      };
      [6] = {
        "Obtenez la carte des Hauts des Galgals Nord - Les Hommes de Bree essayent de se tenir à l'écart des anciens tertres funéraires des Hommes du Cardolan, car on raconte que de terrifiantes créatures sortent de terre pour y traquer ceux qui s'y trouvent. C'est une carte de la partie nord des Hauts des Galgals.";
      };
      [7] = {
        "Obtenez la carte de Fondcombe - Fondcombe est la vallée cachée où demeurent les Elfes, à la frontière des Monts Brumeux et de la Trouée des Trolls. C'est une carte de cette vallée.";
      };
      [8] = {
        "Obtenez la carte d'Eregion - Eregion était la demeure des Elfes d'antan. Aujourd'hui, des ruines recouvrent une terre sauvage. C'est une carte de cette terre.";
      };
      [9] = {
        "Obtenez la carte d'Enedwaith - Portion de terre menant au sud entre Eregion et le Pays de Dun, Enedwaith est une terre sauvage recouverte de pierres. C'est une carte de la région.";
      };
      [10] = {
        "Obtenez la carte de la Porte de Thorin - Cette petite communauté de nains repose dans l'ombre du Palais de Thorin. C'est une carte de cette zone.";
      };
      [11] = {
        "Obtenez la carte de Forochel - Une terre froide et sinistre aux mains des Lossoth, Forochel se trouve à l'extrémité nord. C'est une carte de ces terres gelées.";
      };
      [12] = {
        "Obtenez la carte de Rivegel - Une ville de riches et de pauvres ayant besoin d'aide pendant les mois glaciaux. C'est une carte de la ville.";
      };
      [13] = {
        "Obtenez la carte des Hauts des Galgals Sud - La partie sud des Hauts des Galgals présente davantage de ruines et de tertres des Hommes d'antan. C'est une carte de la zone.";
      };
      [14] = {
        "Obtenez la carte du Pays de Bree - Une terre fertile où beaucoup d'Hommes se sont installés. C'est une carte de la zone.";
      };
      [15] = {
        "Obtenez la carte de la Comté - Un endroit que la plupart des hobbits considèrent comme chez eux. La Comté regroupe plusieurs territoires plus petits qui appartiennent au peuple hobbit. C'est une carte de la zone.";
      };
      [16] = {
        "Obtenez la carte d'Ered Luin - Ered Luin est le foyer des Elfes et des nains, bien que ni les uns ni les autres n'apprécient cette mixité. C'est une carte de la zone.";
      };
      [17] = {
        "Obtenez la carte des Terres Solitaires - Les Terres Solitaires s'étendent vers l'est à travers les ruines des Hommes. C'est une carte de la zone.";
      };
      [18] = {
        "Obtenez la carte des Hauts du Nord - La guerre a frappé les habitants des Hauts du Nord, qui ont besoin d'aide. C'est une carte de la zone.";
      };
      [19] = {
        "Obtenez la carte de la Trouée des Trolls - Cette terre sinistre est habitée par des trolls qui errent nuit et jour dans les terres sauvages. C'est une carte de la zone.";
      };
      [20] = {
        "Obtenez la carte des Monts Brumeux - Le vent froid et les lames travaillées par les gobelins attendent les voyageurs dans les Monts Brumeux. C'est une carte de la zone.";
