  -- Classe / Vacances d'un hobbit
  [1879316232] = {
      [1] = {
        "Obtenez Vacances d'un hobbit";
      };
      [2] = {
        "Allez parler à Bilbon Sacquet à la Dernière Maison Simple de Fondcombe.";
      };
      [3] = {
        "Vacances d'un hobbit, page 66 - Sur cette page, Bilbon détaille un périple terrifiant sur le dos d'un Aigle. Apparemment, il n'en a pas gardé un bon souvenir, et l'Aigle qui l'a transporté non plus.";
      };
      [4] = {
        "Vacances d'un hobbit, page 67 - Sur cette page, Bilbon retranscrit le discours de Gandalf expliquant aux Nains qu'il va devoir les laisser se débrouiller seuls car il a des affaires à régler de son côté.";
      };
      [5] = {
        "Vacances d'un hobbit, page 68 - Sur cette page, Bilbon décrit le plan de Gandalf pour présenter les Nains à Beorn : deux par deux, très lentement, très poliment et sans un mot plus haut que l'autre. Cela donne l'impression que Beorn s'énerve facilement.";
      };
      [6] = {
        "Vacances d'un hobbit, page 69 - Sur cette page, Bilbon décrit l'arrivée à la maison de Beorn, et tout particulièrement les abeilles incroyablement grosses qui y vivent aujourd'hui encore. Beorn était très fier de son élevage d'abeilles, et son fils Grimbeorn l'est tout autant.";
      };
      [7] = {
        "Vacances d'un hobbit, page 70 - Sur cette page, Bilbon raconte sa première rencontre avec Beorn. La carrure et l'assurance de votre parent ont fait forte impression au hobbit, tout comme sa réticence à recevoir le moindre invité. Fort heureusement, le groupe était uniquement constitué de Bilbon et de Gandalf... ou du moins Beorn le croyait-il à ce moment-là.";
      };
      [8] = {
        "Vacances d'un hobbit, page 71 - Sur cette page, Bilbon explique comment Gandalf a fait arriver les autres Nains, si lentement que la curiosité de Beorn en a été piquée et qu'il ne s'est pas mis en colère. Grimbeorn vous a raconté diverses variantes de ce récit durant votre jeunesse.";
      };
      [9] = {
        "Vacances d'un hobbit, page 72 - Sur cette page, Bilbon détaille le repas délicieux servi par Beorn une fois tous les Nains arrivés, et il retranscrit les histoires que votre parent leur a racontées sur la Forêt Noire et les montagnes.";
      };
      [10] = {
        "Vacances d'un hobbit, page 73 - Sur cette page, Bilbon évoque un sommeil troublé perturbé par des grognements et des bruits faits par de nombreux animaux de l'autre côté de la porte. La peur qu'il en a ressentie n'aurait sans doute pas été moins forte s'il avait connu la véritable nature de Beorn, mais vous savez que votre redoutable parent n'a jamais représenté un danger pour le hobbit.";
      };
  };
  -- Classe / Généalogie des Beornides
  [1879316233] = {
      [1] = {
        "Obtenez Généalogie des Beornides";
      };
      [2] = {
        "Allez parler à Bilbon Sacquet à la Dernière Maison Simple de Fondcombe.";
      };
      [3] = {
        "Généalogie des Beornides, page 17 - Cette page comprend la dernière partie des arbres généalogiques, qui amène la lignée de Beorn jusqu'à votre génération : Sterkist, Langhár et vous.";
      };
      [4] = {
        "Généalogie des Beornides, page 18 - Cette page propose plusieurs paragraphes d'informations sur Beorn, votre parent et l'aïeul le plus célèbre de votre lignée. Même vous, vous ignorez tout au sujet des parents de Beorn, et si Grimbeorn sait quelque chose à ce sujet, il ne vous en a jamais parlé.";
      };
      [5] = {
        "Généalogie des Beornides, page 19 - Cette page regroupe plusieurs paragraphes concernant Grimbeorn, le fils de Beorn ainsi que votre parent. C'est le chef ainsi que le patriarche des vôtres, et il réside dans la maison qui était autrefois celle de Beorn. Il est aujourd'hui très vieux, et n'a que peu de patience pour les caprices des jeunes.";
      };
      [6] = {
        "Généalogie des Beornides, page 20 - Cette page ne comprend qu'un seul paragraphe, qui évoque Ofolmóth, qui faisait partie de votre famille, autrefois. Son contenu n'est... plus d'actualité.";
      };
      [7] = {
        "Généalogie des Beornides, page 21 - Cette page offre plusieurs paragraphes d'informations sur votre cousin Varthmath et ses frères et sœurs. La plupart ont tendance à souvent partir loin de la maison, avec pour conséquence que vous ne les connaissez pas aussi bien que Varthmath.";
      };
      [8] = {
        "Généalogie des Beornides, page 22 - Cette page traite plus précisément de votre sœur Langhár, mais il n'y a presque rien d'écrit. Nul doute qu'elle est censée être remplie au fil des exploits de Langhár.";
      };
      [9] = {
        "Généalogie des Beornides, page 23 - Cette page traite plus précisément de votre frère Sterkist, mais il n'y a presque rien d'écrit. Nul doute qu'elle est censée être remplie au fil des exploits de Sterkist. Étonnamment, son nom est mal écrit. Vous décidez de ne pas lui en parler, des fois qu'il le prenne mal.";
      };
      [10] = {
        "Généalogie des Beornides, page 24 - Cette page est sur vous ! Il n'y a presque rien d'écrit, aussi est-elle probablement censée être remplie au fil de vos exploits. Qui sait ? Un jour, peut-être la remplirez-vous.";
      };
  };
  -- Classe / Une étude d'un changeur de peau
  [1879316234] = {
      [1] = {
        "Obtenez Une étude d'un changeur de peau";
      };
      [2] = {
        "Allez parler à Bilbon Sacquet à la Dernière Maison Simple de Fondcombe.";
      };
      [3] = {
        "Une étude d'un changeur de peau, page 4 - Sur cette page, l'auteur affirme que la plupart des changeurs de peau peuvent prendre la forme de divers animaux et qu'ils y gagnent les caractéristiques fondamentales de ces derniers. À votre connaissance, c'est totalement faux.";
      };
      [4] = {
        "Une étude d'un changeur de peau, page 8 - Sur cette page, l'auteur évoque sa certitude que le pouvoir du changeur de peau se transmet uniquement aux hommes. En réalité, même si ce pouvoir est héréditaire, de nombreuses femmes de votre famille l'ont également, y compris votre sœur Langhár.";
      };
      [5] = {
        "Une étude d'un changeur de peau, page 15 - Sur cette page, l'auteur note que, quand un changeur de peau prend la forme d'un animal, il peut devenir bien plus grand que sa taille habituelle. C'est exact, mais vous ne pouvez que vous demander si ceci n'est que pure supposition de la part de l'auteur ou s'il a rencontré des membres de votre famille.";
      };
      [6] = {
        "Une étude d'un changeur de peau, page 16 - Sur cette page, l'auteur explique que tous les changeurs de peau sont bien plus féroces que les gens normaux, ce qui est un mensonge éhonté. De nombreux membres de votre famille sont calmes et portés vers l'introspection. L'auteur ne pourrait qu'être surpris s'il rencontrait votre frère Sterkist, qui passe son temps à rêver d'aventures incroyables mais ne ferait pas de mal à une mouche.";
      };
      [7] = {
        "Une étude d'un changeur de peau, page 23 - Sur cette page, l'auteur explique que les changeurs de peau adorent véritablement les gâteaux au miel. Voilà au moins un détail qui est exact. Votre cousin Varthmath réussit des gâteaux au miel particulièrement délicieux, et ce que vous venez de lire vous a donné envie d'en manger un ! Vous maudissez silencieusement l'auteur de vous avoir rappelé que vous n'aviez pas de gâteau au miel sur vous.";
      };
      [8] = {
        "Une étude d'un changeur de peau, page 42 - Sur cette page, l'auteur décrit le gigantesque élevage d'abeilles dont votre famille s'occupe depuis des générations. Ces abeilles sont incroyablement grosses, et elles font le miel le plus délicieux qu'il vous ait été donné de goûter.";
      };
      [9] = {
        "Une étude d'un changeur de peau, page 11 - Sur cette page, l'auteur pose une question qu'il trouve manifestement terrifiante : se pourrait-il qu'il y ait des changeurs de peau parmi nous ? Vous savez toutefois que les vôtres ne sont pas aussi nombreux qu'il le pense, et que la plupart ne sont pas doués pour s'intégrer au milieu de la populace sans se faire remarquer.";
      };
      [10] = {
        "Une étude d'un changeur de peau, page 21 - Sur cette page, l'auteur faut une étonnante confession : il serait apparemment lui-même un changeur de peau. À en juger par le reste du livre, vous avez vraiment du mal à y croire.";
      };
  };
