  -- Val d'Anduin / Les ennemis du Val
  [1879387432] = {
      [1] = {
      ["NAME"] = "Ylgraf"; 
      ["PROGRESS"] = "Explorez Ylgraf"; 
      ["LORE"] = "Des ouargues se sont souvent aventurés sur les terres des Beornides au fil du temps, mais ceux d'Ylgraf sont aujourd'hui plus gros et plus féroces que leurs congénères. Ces ouargues viennent de Gundabad, et ils rôdent dans les rochers au nord de Beorninghus dans l'espoir d'attaquer Grimbeorn et ses semblables."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "16.76N"; 
          ["LONG"] = "53.60W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Porte des gobelins"; 
      ["PROGRESS"] = "Inspectez la Porte des gobelins"; 
      ["LORE"] = "Les Beornides surveillent la Porte des gobelins, car ce côté des montagnes est la principale entrée de Gobelinville. Au cours des derniers jours de la Guerre de l'Anneau, orques et gobelins ont déferlé de la Porte pour se répandre dans le Val d'Anduin, pillant et brûlant les tranquilles villages des Hommes des bois. Menés par Grimbeorn l'Ancien, les Beornides et les Hommes des bois les ont affrontés et finalement repoussés.\n\nGobelinville est maintenant calme et l'activité à la Porte est quasi nulle, mais les Beornides n'ont pas oublié. Ils la surveillent toujours."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "11.62N"; 
          ["LONG"] = "62.78W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Waldfast"; 
      ["PROGRESS"] = "Explorez Waldfast"; 
      ["LORE"] = "Le paisible village des Hommes des bois de Waldfast s'est fait détruire par une armée de gobelins. Des sentinelles ont été postées dans tout le village, mais elles sont trop peu nombreuses pour expliquer l'étendue des dégâts. Une grotte derrière le village vous apportera peut-être les réponses."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "6.35N"; 
          ["LONG"] = "60.23W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Fil Sulmog"; 
      ["PROGRESS"] = "Explorez Fil Sulmog"; 
      ["LORE"] = "Après les dernières batailles de la Guerre de l'Anneau, un groupe d'orques a établi un campement précaire dans les collines de la Lisière des bois. Il s'agit des Shapag-haï, qui ont décidé qu'ils préféraient s'en prendre aux marchands et aux voyageurs plutôt qu'aux guerriers et aux soldats. Partant de leur camp de Fil Sulmog, ils pratiquent l'art du pillage, pour lequel ils montrent à présent un grand savoir-faire."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "25.59S"; 
          ["LONG"] = "4.98W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Tríubad"; 
      ["PROGRESS"] = "Explorez Tríubad"; 
      ["LORE"] = "Le camp forestier de Tríubad a fourni le bois et les autres matériaux nécessaires aux Hommes des bois pour construire leurs maisons et les murs de leurs villages, mais c'est alors que les Taurogrims sont arrivés. Les Hommes des bois nomment 'Thyrsir' les démons sylvestres, dont ils redoutent le contact fatal.\n\nLes Hommes des bois savent qu'ils ont peut-être perdu Tríubad, mais ils ne passent pas leur temps à se lamenter. Ils cherchent plutôt des aventuriers pour empêcher les Thyrsir de répandre leur mal dans d'autres villages de la Lisière des bois."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "4.04N"; 
          ["LONG"] = "54.48W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Côs Braen"; 
      ["PROGRESS"] = "Explorez Côs Braen"; 
      ["LORE"] = "Côs Braen se trouve au bord sud de Laerlad, terre des Ungoledain et de leurs venimeux semblables. Ils éprouvent une profonde haine envers Grimbeorn, et s'ils le pouvaient, élimineraient Beornides et Gauradans à la première occasion."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "2.59S"; 
          ["LONG"] = "53.48W"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Marécage de Sharcoux"; 
      ["PROGRESS"] = "Explorez le Marécage de Sharcoux"; 
      ["LORE"] = "Des machines de dragage, autrefois manipulées par les Orques et les semi-Orques, aujourd'hui abandonnées, se trouvent dans la boue des Champs aux Iris. Un grand trou a été creusé au Marécage de Sharcoux, et la rumeur affirme qu'un précieux trésor a été trouvé dans ses profondeurs. Les semi-Orques y demeurent, cherchant d'autres richesses pour eux-mêmes et pour leur maître. Ce dernier garde le silence, occupé par autre chose, et ses serviteurs du Marécage ne reçoivent plus de message de sa part depuis quelque temps déjà."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "0.71N"; 
          ["LONG"] = "59.29W"; 
        }; 
      }; 
      };
      [8] = {
      ["NAME"] = "Lúlstrok"; 
      ["PROGRESS"] = "Explorez Lúlstrok"; 
      ["LORE"] = "Des Orques ont fouillé le marécage de longues semaines, opérant depuis leur camp de Lúlstrok dans la partie nord-ouest des Champs aux Iris. L'une des grandes machines de dragage s'y trouve à présent, à l'arrêt, lorsqu'autrefois elle remuait la terre de nuit comme de jour. Armes, armures et autres accessoires ont été transportés à Lúlstrok, mais on ignore où ces trésors se trouvent à présent."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "2.30N"; 
          ["LONG"] = "61.93W"; 
        }; 
      }; 
      };
      [9] = {
      ["NAME"] = "Les terriers de Tûn Melen"; 
      ["PROGRESS"] = "Explorez les terriers de Tûn Melen"; 
      ["LORE"] = "Qu'est-ce qui se cache dans les terriers de Tûn Melen ? Ce n'est qu'en vous y aventurant que vous le saurez, mais les soldats de la Main Blanche y sont peut-être déjà entrés en quête de trésors ou ne serait-ce que pour en savoir plus."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "18.63N"; 
          ["LONG"] = "4.47W"; 
        }; 
      }; 
      };
      [10] = {
      ["NAME"] = "Shathur-munzu"; 
      ["PROGRESS"] = "Explorez Shathur-munzu"; 
      ["LORE"] = "Une brume humide détrempe les pierres menant à Shathur-munzu, l'avant-poste le plus au sud de Khazad-dûm. La forteresse est détruite, et les Nains ne l'occupent plus depuis bien longtemps. Shathur-munzu a été le théâtre de l'une des batailles les plus sanglantes de la guerre entre les Nains et les Orques, et le roi Thrain n'a pas pu la défendre longtemps."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "39.77S"; 
          ["LONG"] = "2.40E"; 
        }; 
      }; 
      };
      [11] = {
      ["NAME"] = "Brafulug"; 
      ["PROGRESS"] = "Explorez Brafulug"; 
      ["LORE"] = "Ugúrz a établi son camp de Brafulug pour attaquer les marchands sur la route, pensant que ses Orques seraient assez loin de la Lothlórien pour échapper à la vigilance des Elfes. Cela pourrait être une erreur."; 
      ["POINTS"] = {
