  -- Val d'Anduin / Sites du Val
  [1879387878] = {
      [1] = {
      ["NAME"] = "L'aire des aigles"; 
      ["PROGRESS"] = "Explorez l'aire"; 
      ["LORE"] = "L’antre des Grands Aigles ne s’atteint que par les airs. C’est là que vivent Gwaihir le Seigneur du Vent et son peuple, peut-être les plus vieux habitants du Val d’Anduin. Ils ont souvent volé loin de leur aire, et leur arrivée a toujours signalé un changement dans le cours du destin : lors des aventures de Bilbon avec Thorin et la Compagnie, quand Gandalf le Gris fut emprisonné par Saroumane le Blanc et dans les dernières heures de la Guerre de l’Anneau.\n\nSi vous rejoignez l’aire des Grands Aigles, faites toujours preuve de la plus grande politesse envers vos hôtes, car ils ne se montrent pas tous amicaux avec les étrangers et les créatures sans ailes."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "14.83N"; 
          ["LONG"] = "61.05W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Haut Col"; 
      ["PROGRESS"] = "Explorez le Haut Col"; 
      ["LORE"] = "Le Haut Col des Monts brumeux demeura traître et périlleux de longues années à cause des gobelins et des géants de pierre qui le hantaient. Quand les Beornides arrivèrent, ils permirent le passage du Haut Col et s’efforcèrent de repousser ses dangers. En échange ils demandèrent un droit de passage considéré comme injuste par les autres peuples.\n\nLe Haut Col resta bloqué pendant la Guerre de l’Anneau, mais aujourd’hui les Beornides l’ont encore sécurisé. Certains disent que quelques géants de pierre ont participé à cet effort, mais le peuple de Grimbeorn refuse de dire si cela est vrai."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "10.17N"; 
          ["LONG"] = "65.61W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Hadhodiant"; 
      ["PROGRESS"] = "Explorez le Hadhodiant"; 
      ["LORE"] = "Le Hadhodiant était un puissant pont des Nains qui permettait de traverser l’Anduin au nord, mais il fut détruit après la chute de Shathur-munzu au Second Age. Il fut reconstruit, et la Grande Alliance l’emprunta en effet en faisant route vers le sud pour assiéger le Mordor.\n\nMais le pont n’allait pas rester longtemps sur pied. Il s’effondra par son milieu, et lorsque des artisans des Monts du Fer vinrent le réparer, des Orques les tuèrent jusqu’au dernier. Le temps passa et les pierres continuaient de tomber dans le fleuve, jusqu’à ce qu’une dernière montée des eaux emportât une bonne partie de ses ruines.\n\nLa destruction du Hadhodiant fit du Vieux Gué le principal endroit où traverser la partie septentrionale du cours de l’Anduin."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "8.60N"; 
          ["LONG"] = "56.59W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Avabarg"; 
      ["PROGRESS"] = "Explorez Avabarg"; 
      ["LORE"] = "Le vieux fort d’Avabarg se trouve sur un îlot du fleuve. Ancienne demeure des Maîtres des Chevaux qui portèrent ensuite le nom d’Éothéod, Avabarg est aujourd’hui vide si l’on excepte l’ombre qui s’est emparée de l’île et les Orques ou gobelins qui tentent parfois de le fortifier pour se l’accaparer.\n\nLes Hommes des bois ont aussi essayé de le reprendre à plusieurs reprises, mais sans succès, et ils en parlent à présent avec beaucoup de peur et de superstition."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "5.01N"; 
          ["LONG"] = "57.14W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Rhosgobel"; 
      ["PROGRESS"] = "Explorez Rhosgobel"; 
      ["LORE"] = "Radagast le Brun, l’un des cinq Magiciens, a élu domicile à Rhosgobel, autrefois le village le plus septentrional des Elfes des bois, puis plus tard le plus méridional. Il choisit d’abord de mener une vie nomade en se basant à Ost Guruth, le fort en ruine des Terres solitaires, mais il est maintenant revenu à la Lisière des bois où il combat l’avancée des ténèbres."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "0.61N"; 
          ["LONG"] = "52.81W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Chutes aux Iris"; 
      ["PROGRESS"] = "Explorez les Chutes aux Iris"; 
      ["LORE"] = "Le cours de la Rivière aux Iris était autrefois plus agité. Lors du combat de la fin du Second Age, Sauron libéra sa sombre influence, pour rallier les créatures du mal à sa cause. On ignore si elles lui répondirent favorablement, mais de redoutables tremblements secouèrent les Monts Brumeux, mettant en péril les palais des Nains jusqu’au Haut Col.\n\nCe tumulte modifia le cours de la rivière, entraînant les sédiments en aval et obstruant l’Étang aux Iris, qui déborda. L’eau se déversa par la falaise, inondant la vallée et submergeant ses routes. C’est ainsi que les Champs aux Iris devinrent un grand marais de roseaux et de sinistres étangs. Avec le temps, le lac retrouva son niveau, et les Chutes aux Iris devinrent le petit torrent que l'on connaît aujourd’hui."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "0.92N"; 
          ["LONG"] = "63.58W"; 
        }; 
      }; 
      };
  };
