  -- Puits du Long Fleuve / Les campements des Puits du Long Fleuve
  [1879405913] = {
      [1] = {
      ["NAME"] = "Limlók"; 
      ["PROGRESS"] = "Explorez le village beornide de Limlok"; 
      ["LORE"] = "Suite à la mort de leur chef, Bolg, au cours de la Bataille des Cinq Armées, les Orques des Monts Brumeux sombrèrent dans le déclin. Leur affaiblissement ne durerait pas éternellement, mais le peuple de Beorn en profita pour se réapproprier la Lande inondée et les contrées sauvages. C'est ainsi que le village de Limlok vit le jour, devenant la localité la plus au nord du peuple de Beorn.\n\nC'est dans la Lande inondée que les Beornides sont les plus féroces, car ils sont en accord avec la nature, qui y est sauvage. Aujourd'hui, Limlok est dirigé par Runerik Barbe-Nouée, qui se méfie des étrangers, persuadé qu'il est que les siens sont assez forts pour repousser, seuls, les pillards venus de Gundabad ou d'ailleurs."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "20.83N"; 
          ["LONG"] = "56.02W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Lyndelby"; 
      ["PROGRESS"] = "Explorez le village caché de Lyndelby"; 
      ["LORE"] = "Ce hameau des Smials est niché entre des falaises, caché jusqu'à ce jour aux yeux des Sages et protégé de toute découverte par ses Aigles gardiens juchés sur les pics. Serait-ce le dernier refuge des Holbytlan qui prospéraient autrefois en Havrebrume et dans la Lande inondée, et qui apparaissent si souvent dans les récits des Éothéod ? Alors, ce hameau serait celui des descendants des Pâles, qui ne traversèrent pas les Monts Brumeux pour aller peupler ce qui deviendrait la Comté."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "26.68N"; 
          ["LONG"] = "65.73W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Thokvist"; 
      ["PROGRESS"] = "Explorez Thokvist"; 
      ["LORE"] = "Skarding Fléau-des-Loups vit à Thokvist depuis de longues années, mais ce petit campement est souvent laissé sans surveillance. Le Beornide passe le plus clair de son temps à chasser les menaces qui pèsent sur les vallons de Havrebrume. Lorsqu'il rentre enfin chez lui, il ne fait que passer, se contentant sûrement d'une des pommes poussant non loin de là et d'un repos aussi bref que mérité."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "25.92N"; 
          ["LONG"] = "58.37W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Hlithseld"; 
      ["PROGRESS"] = "Explorez Hlithseld"; 
      ["LORE"] = "Peut-être que dans un proche avenir, quand Léothred et ses recherches auront levé le voile sur l'histoire des Éothéod, le nom d'Hlithseld sera communément tenu en haute estime. 'C'est là qu'il campa pendant sa quête !' diront peut-être les gens... ou il n'en sera rien. Difficile de dire ce dont les gens se souviendront."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "30.48N"; 
          ["LONG"] = "59.25W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Shulgmokh"; 
      ["PROGRESS"] = "Explorez Shulgmokh"; 
      ["LORE"] = "Les Beornides de la Lande inondée ignorent si les trolls des bois de Shulgmokh sont en lien avec les Taurogrims des autres terres, et ne s'en soucient guère. Le temps qu'il faut pour arracher les duramens dont ils dépendent, et leur envie de stopper ces créatures qui menacent Limlok les préoccupent bien davantage."; 
