      [6] = {
      ["NAME"] = "Tharkh Shathur"; 
      ["PROGRESS"] = "Informez-vous sur Tharkh Shathur"; 
      ["LORE"] = "'Jadis, ce col menait à Atrad-zarakh, mais une avalanche sur les pentes de Shathûr l'a condamné pour longtemps suite à la chute de la Moria. En érigeant l'un de leurs ponts rudimentaires pour accéder à l'ancien col, il semblerait que les Orques nous aient, sans le vouloir, rendu un fier service !'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "68.17N"; 
          ["LONG"] = "132.19W"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Ashmargathâl"; 
      ["PROGRESS"] = "En savoir plus sur Ashmargathâl"; 
      ["LORE"] = "'Avec l'aide des Kambrada, nous avons pris pied sur les pentes de Bundushathûr. Le grand poste de garde d'Ashmargathâl était tombé aux mains des Orques mais nous l'avons reconquis. Las, la tour de surveillance qui se trouve plus loin sera difficile à reprendre.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "69.52N"; 
          ["LONG"] = "133.25W"; 
        }; 
      }; 
      };
      [8] = {
      ["NAME"] = "Atrad-zarakh, la Tour tutélaire"; 
      ["PROGRESS"] = "Informez-vous sur Atrad-zarakh"; 
      ["LORE"] = "'C'est à Atrad-zarakh, la tour de surveillance, que les grandes cloches de Khazad-dûm résonnaient jadis d'un bout à l'autre d'Azanulbizar, d'abord les jours de fête puis, plus tard, en guise d'avertissement, lorsque la chute de la Moria était proche. Hélas ! La tour est désormais aux mains de l'ennemi.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "69.36N"; 
          ["LONG"] = "134.68W"; 
        }; 
      }; 
      };
      [9] = {
      ["NAME"] = "Tharkh Baraz"; 
      ["PROGRESS"] = "Informez-vous sur Tharkh Baraz"; 
      ["LORE"] = "'De tous les cols jalonnant les sommets de Khazad-dûm, le plus emprunté jadis fut celui de Tharkh Baraz, aussi appelé Col du Rubicorne. Je nourris l'espoir qu'il soit un jour possible de le franchir en toute sécurité.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "69.37N"; 
          ["LONG"] = "137.47W"; 
        }; 
      }; 
      };
      [10] = {
      ["NAME"] = "Anlakh-zeleg"; 
      ["PROGRESS"] = "Informez-vous sur Anlakh-zeleg"; 
      ["LORE"] = "'Même la corruption des Orques n'a pas pu altérer la beauté d'Anlakh-zeleg, l'Escalier de Dimrill ! Au pied de Tharkh Baraz, ses chutes miroitent au soleil et se jettent dans le Lac du Miroir.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "69.87N"; 
          ["LONG"] = "136.15W"; 
        }; 
      }; 
      };
      [11] = {
      ["NAME"] = "Tharkh Zigil"; 
      ["PROGRESS"] = "Informez-vous sur Tharkh Zigil"; 
      ["LORE"] = "'Tharkh Zigil reste un lieu mystérieux. Ce passage taillé dans les pentes de Zirakzigil est tombé dans l'oubli bien avant la chute de la Moria. Peut-être menait-il autrefois à la tour de Durïn, mais je n'ai pas encore découvert sa véritable raison d'être.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "66.81N"; 
          ["LONG"] = "137.47W"; 
        }; 
      }; 
      };
      [12] = {
      ["NAME"] = "Zirakazhâr"; 
      ["PROGRESS"] = "Informez-vous sur Zirakazhâr"; 
      ["LORE"] = "'Zirakazhâr, jadis armurerie et poste de guet au sud de Khazad-dûm, est tombé aux mains des Orques peu après la chute de la Moria. Nous devons le reprendre si nous voulons avoir une chance de vaincre les armées d'Azog !'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "66.22N"; 
          ["LONG"] = "136.70W"; 
        }; 
      }; 
      };
      [13] = {
      ["NAME"] = "Kheled-zâram, le Lac du Miroir"; 
      ["PROGRESS"] = "Informez-vous sur Kheled-zâram"; 
      ["LORE"] = "'Ah, le Lac du Miroir ! Ses eaux ont de toute évidence fait l'objet d'un enchantement car il reflète en permanence un ciel nocturne. C'est là que Durïn l'Immortel a vu pour la première fois sa tête couronnée d'étoiles avant de creuser, dans sa grande sagesse, les premières salles de Khazad-dûm.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "67.25N"; 
          ["LONG"] = "134.62W"; 
        }; 
      }; 
      };
      [14] = {
      ["NAME"] = "Durinul’aban"; 
      ["PROGRESS"] = "Informez-vous sur Durinul’aban"; 
      ["LORE"] = "'Cela fait des années qu'un Nain n'a plus posé les yeux sur Durin'abal. La Pierre de Durïn marque l'endroit où se tenait Durïn l'Immortel lorsqu'il plongea pour la première fois son regard dans le Kheled-zâram. La voir m'apporte un grand réconfort, même en ces temps de guerre.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "66.50N"; 
          ["LONG"] = "134.98W"; 
        }; 
      }; 
      };
      [15] = {
      ["NAME"] = "Nud-mekhem, la porte est"; 
      ["PROGRESS"] = "Informez-vous sur Nud-mekhem"; 
      ["LORE"] = "'Des siècles ont passé mais la façade de Nud-mekhem, la grande porte est, n'est pas complètement tombée en ruines malgré les efforts des Orques. Les portes ont été souillées d'inscriptions en orque, mais rien de ce que font ces créatures et leurs semblables ne dure très longtemps !'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "67.60N"; 
          ["LONG"] = "137.52W"; 
        }; 
      }; 
      };
