      [3] = {
      ["NAME"] = "Dursh Hûl"; 
      ["PROGRESS"] = "Informez-vous sur Dursh Hûl"; 
      ["LORE"] = "'Au nord de Khur Azan, nous découvrîmes que le vieux pont avait été détruit. Les Orques avaient à sa place construit un gué pour garder le col vers Shathûr.'"; 
