  -- Gundabad / L'ultime frontière d'Angmar
  [1879422517] = {
      [1] = {
      ["NAME"] = "Rathad Caul, la Voie étroite"; 
      ["PROGRESS"] = "Explorez Rathad Caul, la Voie étroite"; 
      ["LORE"] = "Entre les terres de Val-d'Aïeul et de Câr Bronach se trouve un passage fortifié connu sous le nom de Rathad Caul, la Voie étroite. Entouré de falaises abruptes, Rathad Caul a été ouvert il y a longtemps et abandonné après qu'Angmar ait livré plusieurs batailles sanglantes contre les Zhelruka. Comme il faisait partie de la vaste frontière d'Angmar sous le règne du Roi-Sorcier, Rathad Caul marque également le point le plus à l'ouest de Men Angrun, une ancienne route reliant Angmar à Rhûn qui partait autrefois de Câr Bronach."; 
      ["POINTS"] = {
