  -- Classe / Déplacement martial
  [1879428104] = {
      [1] = {
        "Obtenez Déplacement martial";
      };
      [2] = {
        "Demandez des explications à Brom";
      };
      [3] = {
        "Déplacement martial, page 27 - Sur cette page, Brom traite de l'importance de savoir quand esquiver une attaque. 'Selon l'angle de l'attaque,' écrit-il, 'vous pourriez être tenté de vous baisser ! Toutefois, si votre adversaire vous porte un grand coup vertical avec un objet lourd, un rapide pas sur le côté pourrait être plus approprié.'";
      };
      [4] = {
        "Déplacement martial, page 33 - Brom consacre la majorité de l'espace disponible sur cette page à décrire une bagarre particulièrement mémorable de sa jeunesse. 'Vous auriez dû voir ça,' écrit-il. 'Les chaises volaient dans tous les sens, mais elles ne représentaient pas plus pour nous que de simples volutes de fumée, en cela que pas une ne pouvait nous approcher !'";
      };
      [5] = {
        "Déplacement martial, page 34 - Sur cette page, Brom a schématisé bien trop de pieds pour qu'un lecteur puisse y comprendre quelque chose. 'Comme vous pouvez le voir', explique-t-il, 'il est crucial de garder les pieds en mouvement lorsque vous vous battez.'";
      };
      [6] = {
        "Déplacement martial, page 35 - Sur cette page, Brom se penche sur un certain nombre de feintes qu'un bagarreur peut exécuter pour désorienter son adversaire. Parmi ces diversions, on retiendra entre autres le 'Oops, pas par là !', le 'Par ici, eh non, par là !' et le 'Ça va trop vite pour toi'.";
      };
      [7] = {
        "Déplacement martial, page 36 - Sur cette page, Brom affirme être à l'origine du célèbre adage selon lequel 'la meilleure défense, c'est l'attaque'. À votre connaissance, c'est faux. Vers le bas de la page, Brom affirme tout de même que 'la meilleure défense, c'est la défense'. Juste au cas où.";
      };
      [8] = {
        "Déplacement martial, page 52 - Sur cette page, Brom suggère au lecteur de privilégier les jours de grand vent pour s'exercer aux manœuvres les plus agiles. 'De cette manière', affirme-t-il, 'vous serez encore plus rapide lorsque le temps sera clément et l'air statique !'";
      };
      [9] = {
        "Déplacement martial, page 56 - Sur cette page, Brom mentionne une troublante situation hypothétique. 'Dans l'éventualité où vous auriez à combattre un bagarreur ayant lui aussi étudié le contenu de cet ouvrage, vous pourriez vous retrouver prisonniers d'une danse infinie d'esquives et de dérobades, sans qu'aucun des deux combattants ne parvienne à porter un coup efficace !' Brom ne donne aucun conseil pour se sortir de cette situation. Il semble simplement avoir trouvé intéressante l'idée que cela puisse se produire.";
      };
      [10] = {
        "Déplacement martial, page 58 - Sur cette page, Brom insiste sur l'importance de rester pleinement concentré lors d'un affrontement où les coups pleuvent de tous les côtés. 'Seule une conscience aiguë de votre environnement vous permettra d'esquiver tout projectile lancé dans votre direction,' explique-t-il. En bas de la page, il semble se contredire. 'Parfois, il faut savoir compter sur la chance,' écrit-il. 'Mais en fin de compte, le plus important est évidemment de lire attentivement des textes tels que celui-ci. Merci d'avoir acheté mon livre !'";
      };
  };
  -- Classe / Connaissance des coups de pied
  [1879428111] = {
      [1] = {
        "Obtenez Connaissance des coups de pied";
      };
      [2] = {
        "Demandez à Brom pourquoi tant de pages manquent à ses traités";
      };
      [3] = {
        "Connaissance des coups de pied, page 7 - Sur cette page, Brom recommande une méthode simple pour améliorer la précision de vos coups de pied : placez une pomme sur un piquet de clôture en bois, fermez les yeux et essayez de faire tomber le fruit avec votre pied. 'Si vous y parvenez,' explique Brom, 'recommencez avec des pommes de plus en plus petites et des piquets de plus en plus hauts.'";
      };
      [4] = {
        "Connaissance des coups de pied, page 10 - Sur cette page, Brom se livre à une longue digression au sujet des coups de poing, sans mentionner les coups de pied. Au bas de la page, il recommande au lecteur avide de connaissances sur le combat au corps-à-corps de consulter un autre de ses livres, intitulé 'L'Art du coup de poing parfait'. Vous vous demandez si ce livre est plein d'informations sur les coups de pied. Plus encore, vous aimeriez savoir qui a bien pu suggérer à Brom d'écrire ces livres.";
      };
      [5] = {
        "Connaissance des coups de pied, page 12 - Sur cette page, Brom a réalisé plusieurs dessins visant à expliquer la manière idéale d'exécuter trois puissants coups de pied. Malheureusement, il est bien moins doué pour les arts visuels que pour les arts martiaux, et les croquis sont totalement incompréhensibles.";
      };
      [6] = {
        "Connaissance des coups de pied, page 18 - Sur cette page, Brom détaille les problèmes et les pièges que rencontrent invariablement les bagarreurs tentant d'exécuter un coup de pied en sautant. 'À moins que vous ne soyez particulièrement rapide,' écrit-il, 'votre adversaire risque de vous attraper la jambe avant que vous n'ayez pu le toucher, et vous ne pourrez plus faire grand-chose. Laissez cette technique aux experts !'";
      };
      [7] = {
        "Connaissance des coups de pied, page 19 - Sur cette page, dans ce qui semble être un véritable cri du cœur, Brom poursuit sa diatribe contre les coups de pied sautés. 'Lecteur, lectrice, faites-moi confiance. Vous ne voudriez pas sauter sur quelqu'un la jambe tendue et vous faire attraper en plein air. Une telle situation serait des plus humiliantes... je suppose.' Vous soupçonnez l'auteur de Connaissance des coups de pied de s'être un jour essayé à une telle manœuvre, sans succès.";
      };
      [8] = {
        "Connaissance des coups de pied, page 25 - Sur cette page, Brom traite de l'importance de frapper aussi bien de la jambe gauche que de la jambe droite. 'Mais pas les deux à la fois,' précise-t-il.";
      };
      [9] = {
        "Connaissance des coups de pied, page 35 - Sur cette page, Brom compare l'art de combattre avec les pieds à celui de la danse. 'Mais pas de celles qu'ils pratiquent à la cour,' poursuit-il. 'Se bagarrer avec les pieds est bien plus proche des danses que l'on pratique dans les tavernes de campagne... ce qui est bien plus amusant !'";
      };
      [10] = {
        "Connaissance des coups de pied, page 37 - Sur cette page, Brom recommande l'achat d'un autre de ses livres, intitulé Déplacement martial. Dans les pages de cet 'excellent investissement', il promet d'expliquer un certain nombre de manœuvres qui vous permettront d'esquiver les coups de pied que vous avez appris dans le présent ouvrage. 'Mais n'ayez crainte,' écrit-il', vos adversaires, eux, ne l'auront pas lu !'";
      };
  };
  -- Classe / L'Art du coup de poing parfait
  [1879428116] = {
      [1] = {
        "Obtenez L'Art du coup de poing parfait";
      };
      [2] = {
        "Faites part de votre mécontentement à Brom Barbe-de-faucon dans la Prison de Bree";
      };
      [3] = {
        "L'Art du coup de poing parfait, page 6 - Sur cette page, Brom aborde un certain nombre de techniques qu'un bagarreur peut employer pour entamer un combat. La principale se nomme 'regarde cette grive !'. Pour cette manœuvre maintes fois éprouvée, le bagarreur fait mine de tenir un petit oiseau dans sa main gauche et en profite pour frapper soudainement de la droite.";
      };
      [4] = {
        "L'Art du coup de poing parfait, page 7 - Sur cette page, Brom liste un certain nombre de mouvements à effectuer contre un adversaire consumé par la rage suite à l'exécution réussie de la manœuvre 'regarde cette grive', expliquée en détail sur une page précédente. La plupart de ces techniques semblent reposer sur une retraite précipitée.";
      };
      [5] = {
        "L'Art du coup de poing parfait, page 15 - Sur cette page, Brom disserte sur les différences entre une 'beigne' et un 'pain', deux termes qui, artistiquement, laissent fortement à désirer.";
      };
      [6] = {
        "L'Art du coup de poing parfait, page 19 - Sur cette page, Brom pose la question suivante : 'Le coup le plus important d'un combat n'est-il pas celui que vous n'avez jamais besoin porter ?' Sa réponse : 'Non.'";
      };
      [7] = {
        "L'Art du coup de poing parfait, page 22 - Sur cette page, Brom recense plusieurs points vulnérables qu'un bagarreur compétent devrait cibler pour affaiblir son opposant. Malheureusement, sa compréhension de l'anatomie est assez approximative, et la plupart de ses conseils ne s'appliquent qu'aux adversaires humanoïdes.";
      };
      [8] = {
        "L'Art du coup de poing parfait, page 23 - Sur cette page, Brom recommande à son lecteur, dans le cas où celui-ci aurait l'intention de se battre contre des araignées ou autres insectes rampants, d'acheter un autre de ses ouvrages, intitulé Connaissance des coups de pied, dans lequel il trouvera des conseils sur la manière de vaincre ces créatures. 'Je ne décrirai pas ici le type d'attaque que vous devrez maîtriser', écrit-il, 'mais il ne s'agira certainement pas d'un coup de poing !' Vous suspectez là une allusion aux coups de pied, mais comme toujours avec Brom, difficile d'en être sûr.";
      };
      [9] = {
        "L'Art du coup de poing parfait, page 24 - Sur cette page, Brom souligne que de nombreux bagarreurs ne parviennent jamais à maîtriser l'art du coup de poing parfait, et ce même en ayant étudié auprès des meilleurs entraîneurs de la Terre du Milieu. 'Cela témoigne des piètres compétences de l'élève, et non pas de celles du maître', insiste-t-il.";
      };
      [10] = {
        "L'Art du coup de poing parfait, page 49 - Sur cette page, Brom traite de la nécessité de porter des coups successifs en alternant les poings. Vous vous demandez pourquoi il a attendu d'arriver à la page 49 pour décrire cet élément important du combat.";
      };
  };
