      [2] = {
      ["NAME"] = "Le Trésor caché d'Abnankâra"; 
      ["PROGRESS"] = "Découverez le Trésor caché d'Abnankâra"; 
      ["LORE"] = "Au plus profond des cavernes de Fond-de-fosse repose Abnankâra, le Trésor caché. Il a été amassé dans le plus grand secret par Motsog et les Grises-Masses pendant la rébellion contre les Longues Barbes dans les Temps Anciens. Ce butin devait être un somptueux trésor, le joyau d’un tout nouveau royaume reposant au cœur du mont Gundabad. Après la défaite de Motsog et des Grises-Masses à la Séparation, Abnankâra fut abandonné et oublié jusqu’à sa découverte plus tard durant le Troisième Âge."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "51.07N"; 
          ["LONG"] = "45.31E"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Ibkêrmazal"; 
      ["PROGRESS"] = "Découvrez Ibkêrmazal"; 
      ["LORE"] = "Au sein du Trésor caché d’Abnankâra, Ibkêrmazal servait d’armurerie et de rempart contre ceux qui tenteraient de piller les richesses des Grises-Masses. Sous les ordres de Motsog, la grande salle fût divisée en trois parties, chacune équipée de trous de loup et de quartiers dissimulés, pour diviser et acculer les envahisseurs. Dans le pire des cas, ça servira à transpercer le ventre moelleux des dragons."; 
      ["POINTS"] = {
