      [4] = {
      ["NAME"] = "Refuge des Cuan-lûth"; 
      ["PROGRESS"] = "Explorez le refuge de Cuan-lûth"; 
      ["LORE"] = "Ancien refuge occupé par les Rôdeurs de Tornhad. Cette caverne accueille à présent des réfugiés de Cuan-lûth refusant de s’agenouiller devant Saruman."; 
      ["POINTS"] = {
