  -- Est du Gondor royal / La reconquête du Lossarnach
  [1879464437] = {
      [1] = {
      ["NAME"] = "Arnach"; 
      ["PROGRESS"] = "Trouvez Arnach"; 
      ["LORE"] = "Le seigneur Forlong, le souverain du Lossarnach, avait une résidence au village d'Arnach jusqu'à ce qu'il se rende à Minas Tirith pendant la Guerre de l'Anneau. Il périt lors de la bataille du Pelennor et sa résidence est désormais vide, les habitants d'Arnach s'efforçant de continuer à vivre sans leur seigneur bien-aimé. Arnach reste un village dynamique de marchands et d'artisans, mais le souvenir des attaques des Haradrim au cours des derniers mois reste douloureux."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "76.55S"; 
          ["LONG"] = "24.40W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Imloth Melui"; 
      ["PROGRESS"] = "Trouvez Imloth Melui"; 
      ["LORE"] = "Imloth Melui, le Val des Douces Fleurs, est la demeure de Dame Vanyalos, sœur de l'intendant Denethor et épouse de Forlong, seigneur du Lossarnach. Ces deux hommes ont péri pendant la guerre, l'un à l'intérieur des murs de la Cité Blanche et l'autre à l'extérieur, mais les cicatrices laissées par leur disparition sont particulièrement douloureuses pour Vanyalos, qui a rêvé que cela arriverait et ressent le fardeau de n'avoir pas pu l'empêcher.\n\nC'est à Imloth Melui que sont formés la plupart des guérisseurs qui travaillent dans les Maisons de Guérison de Minas Tirith, y compris Ioreth, une femme qui en sait un peu sur un nombre impressionnant de choses, mais beaucoup sur très peu de choses."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "73.20S"; 
          ["LONG"] = "24.79W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Nan Harlond"; 
      ["PROGRESS"] = "Trouvez Nan Harlond"; 
      ["LORE"] = "Les champs et les fermes de Nan Harlond bordent la route, en contrebas du port du sud du Gondor. Ils ne sont plus menacés par les armées de Sauron, mais le sont encore par d'occasionnels traînards, abandonnés par leurs alliés vaincus. La beauté naturelle du Lossarnach se bat encore pour émerger après la Guerre de l'Anneau, et les fleurs ici témoignent de sa résilience."; 
      ["POINTS"] = {
