  [1879464470] = {
      [1] = {
      ["NAME"] = "Minlos"; 
      ["PROGRESS"] = "Trouvez Minlos"; 
      ["LORE"] = "Le phare de Minlos est le plus proche de Minas Tirith et fut saisi par des rôdeurs haradrim pendant la guerre, alors qu'ils tentaient de s'attaquer au village d'Arnach. Aujourd'hui, il est éteint et les habitants des fermes voisines espèrent qu'il le restera pendant de nombreuses années, signe que le danger est passé."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "76.83S"; 
          ["LONG"] = "25.96W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Halach"; 
      ["PROGRESS"] = "Trouvez Halach"; 
      ["LORE"] = "Le petit port de Halach a été abandonné aux mains des Haradrim pendant la guerre, mais la défaite de Sauron a permis aux habitants de revenir et d'entamer le long processus de reconstruction."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "82.26S"; 
          ["LONG"] = "26.21W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Glaniath"; 
      ["PROGRESS"] = "Trouvez Glaniath"; 
      ["LORE"] = "La petite communauté agricole de Glaniath a été menacée par des pillars haradrim pendant la guerre, et ses habitants tentent à présent de se reconstruire. Certains pensent que la période que Sirgon a passée en captivité aux mains des Haradrim l'a rendu inapte à gouverner et que son fils Faltharan devrait prendre sa place en tant que seigneur du Lebennin."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "80.62S"; 
          ["LONG"] = "29.70W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Tumladen"; 
      ["PROGRESS"] = "Trouvez Tumladen"; 
      ["LORE"] = "Tumladen est le grand refuge du Gondor, semblable au Gouffre de Helm dans le royaume du Rohan. Pendant la guerre, de nombreuses personnes se sont réfugiées à Tumladen, y compris l'une des sœurs de l'intendant Denethor, l'opiniâtre Dame Terenis. Au grand soulagement du capitaine Imladir, responsable de la défense de Tumladen, Dame Terenis est maintenant retournée à Minas Tirith. Le sanctuaire n'est plus encombré par les réfugiés du Gondor."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "76.84S"; 
          ["LONG"] = "29.35W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Pelargir"; 
      ["PROGRESS"] = "Trouvez Pelargir"; 
      ["LORE"] = "La ville portuaire de Pelargir a été le théâtre de la bataille décisive contre les corsaires d'Umbar pendant la Guerre de l'Anneau, lorsqu'Aragorn a ordonné à l'armée des Morts de s'emparer de leurs navires. Balakhôr le Fléau a trouvé la mort à bord de son grand navire, le Joyau de nuit, et l'on espère que sa mort marquera la fin de la menace posée par les Héritiers de Castamir.\n\nLa flotte du Gondor est en ruine depuis la Guerre de l'Anneau, et c'est à Sirgon, le seigneur du Lebennin, qu'incombera la tâche de reconstruire les navires et la puissance maritime du royaume le plus rapidement possible. C'est pour cette raison que dans la ville de Pelargir résonne le bruit des marteaux et des haches à toute heure du jour et de la nuit."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "84.01S"; 
          ["LONG"] = "33.18W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Sirthanc"; 
      ["PROGRESS"] = "Trouvez Sirthanc"; 
      ["LORE"] = "Le feu d'alarme de Sirthanc a été allumé pendant la guerre pour appeler les guerriers à défendre Minas Tirith, mais il est maintenant éteint. Si la bonne fortune du Gondor perdure, il restera éteint pendant de nombreuses années. C'est à Sirthanc que le seigneur Sirgon a été sauvé de la captivité des Haradrim, et certains de ses concitoyens pensent maintenant qu'il devrait être autorisé à quitter son poste de seigneur de Lebennin afin de pouvoir se concentrer sur son rétablissement, à la fois physique et mental."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "79.35S"; 
          ["LONG"] = "31.96W"; 
        }; 
      }; 
      };
  };
