  -- Est du Gondor royal / Reconquête du Bas-Lebennin
  [1879464488] = {
      [1] = {
      ["NAME"] = "Erynos"; 
      ["PROGRESS"] = "Trouvez Erynos"; 
      ["LORE"] = "Le village d'Erynos a été le théâtre de l'un des événements les plus étranges de la guerre, lorsqu'un certain nombre de pillards corsaires ont été découverts à l'extérieur des murs de la ville, apparemment noyés. Certains pensent qu'il s'agit de l'œuvre des Cinq Sœurs du Lebennin, les filles de l'eau qui selon la légende protègent les terres traversées par leurs rivières, mais d'autres affirment qu'elles ne sont qu'un mythe.\n\nAujourd'hui, les habitants d'Erynos s'efforcent de retrouver une vie normale et d'aider leurs voisins qui ont souffert de la guerre."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "80.08S"; 
          ["LONG"] = "32.80W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Ost Anglebed"; 
      ["PROGRESS"] = "Trouvez Ost Anglebed"; 
      ["LORE"] = "La grande ville d'Ost Anglebed abrite les gardiens du feu d'alarme d'Amon Anglebed, allumé pendant la Guerre de l'Anneau. Contrairement aux autres feux du Gondor, il reste allumé malgré le retour de la paix. Les gardes surveillent désormais les habitants, craignant que certains alliés du déloyal Malthellim ne pensent à marcher sur ses traces et à confier le contrôle de la ville aux ennemis du Gondor."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "78.98S"; 
          ["LONG"] = "39.14W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Malbarth"; 
      ["PROGRESS"] = "Trouvez Malbarth"; 
      ["LORE"] = "Le petit village de Malbarth a échappé à la plupart des combats pendant la guerre, mais des nouvelles en provenance d'autres régions du Gondor ont rempli les habitants d'inquiétude et de crainte que leurs maisons ne restent pas longtemps en sécurité. Aujourd'hui encore, des rumeurs affirment que des corsaires ont fui la bataille de Pelargir et rôdent dans la nature, attendant une occasion de s'emparer du village."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "80.14S"; 
          ["LONG"] = "42.38W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Linhir"; 
      ["PROGRESS"] = "Trouvez Linhir"; 
      ["LORE"] = "La ville de Linhir borde la rivière Gilrain, et pendant la Guerre de l'Anneau, le ciel y fut assombri par les armées de Sauron. Les fils de Comeldir ont organisé les défenses, et lorsque les combats ont enfin pris fin, les habitants sont restés forts, moins divisés que leur ville. Aujourd'hui, la menace de la guerre étant écartée et le ciel redevenu clair et lumineux, les habitants de Linhir ont repris le cours de leur vie."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "81.41S"; 
          ["LONG"] = "45.43W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Sarnhad"; 
      ["PROGRESS"] = "Trouvez Sarnhad"; 
      ["LORE"] = "Ce petit village sur la rivière Serni a échappé à la plupart des combats pendant la guerre, mais les nouvelles de corsaires et de semi-trolls attaquant les villages voisins remplissaient les habitants d'effroi, tout comme le ciel noir de la Journée sans Aube. Aujourd'hui, les habitants de Sarnhad cherchent à retrouver leur vie d'avant."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "81.59S"; 
          ["LONG"] = "43.90W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Zarsatrâd"; 
      ["PROGRESS"] = "Trouvez Zarsatrâd"; 
      ["LORE"] = "Niché dans la forêt au sud du bastion nain de Furukzahar, Zarsatrâd sert d'avant-poste aux Nains de Temamir qui vivent désormais dans les Montagnes Blanches. Ils transportent les pierres extraites dans diverses régions du Gondor, désireux d'aider leurs voisins à se reconstruire après la Guerre de l'Anneau... contre rémunération, bien sûr."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "77.80S"; 
          ["LONG"] = "34.56W"; 
        }; 
      }; 
      };
      [7] = {
      ["NAME"] = "Furukzahar"; 
      ["PROGRESS"] = "Trouvez Furukzahar"; 
      ["LORE"] = "La forteresse de Furukzahar est un bastion sûr pour les Nains des Montagnes Blanches de Temamir. Sous le règne de leur chef Hamisar, les Nains de tous les clans sont les bienvenus, mais les relations avec le peuple du Gondor ne sont pas particulièrement chaleureuses en ce moment. La décision de Hamisar de fermer les portes de Furukzahar et de ne pas s'impliquer dans la Guerre de l'Anneau n'a pas rendu les Nains très sympathiques aux yeux des habitants du Lebennin."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "70.34S"; 
          ["LONG"] = "35.35W"; 
        }; 
      }; 
      };
  };
