      [3] = {
      ["NAME"] = "Hata Kebir"; 
      ["PROGRESS"] = "Trouvez Hata Kebir"; 
      ["LORE"] = "Le camp de guerre de Hata Kebir était l'un des principaux établissements construits par les Haradrim et les corsaires qui s'unirent pour envahir le Lebennin et les vastes contrées du Gondor. Depuis la fin de la guerre, des brigands s'y sont rassemblés et mettent en péril la paix apportée par le roi."; 
      ["POINTS"] = {
