      };
      [4] = {
      ["NAME"] = "Ruines d'Ethillorn"; 
      ["PROGRESS"] = "Découvrez les ruines d'Ethillorn"; 
      ["LORE"] = "Les corsaires d'Umbar ont brûlé le port d'Ethillorn pendant leur tentative de conquête du Gondor pour le compte de Sauron. Maintenant que la guerre est terminée, les corsaires sont partis, mais un mal plus sombre s'est abattu sur Ethillorn et menace désormais les terres environnantes."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "83.85S"; 
          ["LONG"] = "45.62W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Ghyll d'Orlach"; 
      ["PROGRESS"] = "Trouvez le Ghyll d'Orlach"; 
      ["LORE"] = "Attirées loin des sommets enneigés des Montagnes Blanches par l'agitation qui règne à la carrière d'Aban-bazan, de féroces bêtes des neiges ont établi leurs tanières dans les bois du Lebennin. De nombreux os rongés et carcasses répugnantes gisent sous les arbres et entre les rochers, témoignant de la faim insatiable de ces créatures."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "74.96S"; 
          ["LONG"] = "35.77W"; 
        }; 
      }; 
      };
