  -- Ouest du Gondor royal / Exploration de la Vallée de la Racine Noire renaissante
  [1879466366] = {
      [1] = {
      ["NAME"] = "Fennas Dûr"; 
      ["PROGRESS"] = "Trouvez Fennas Dûr"; 
      ["LORE"] = "Au seuil de la Porte Ténébreuse et dans la pénombre de la forêt de Dimholt au Rohan, se dressent les Chemins des Morts, d'imposants tunnels creusés sous le Dwimorberg, la Montagne Hantée, venant s'étendre jusqu'à Fennas Dûr, dans la Vallée de la Racine Noire du Gondor. La plupart des Morts de Dunharrow ont traversé Fennas Dûr aux côtés du roi Elessar afin d'atteindre la Pierre d'Erech et enfin honorer leur serment à Pelargir. Hélas, les quelques impénitents qui ont manqué une seconde fois à leur parole ont scellé leur destin de leur propre gré. Aujourd'hui, ces Morts Impitoyables rôdent encore, hantant les ombres de la Vallée de la Racine Noire, incertains si la malédiction d'Isildur sera jamais levée… et peut-être indifférents à ce fait."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "53.15S"; 
          ["LONG"] = "66.85W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Morlad"; 
      ["PROGRESS"] = "Trouvez Morlad"; 
      ["LORE"] = "Sur les pentes au sud des Montagnes Blanches, le long de la rivière Morthond, se trouve le siège de la Vallée de la Racine Noire, la cité fortifiée de Morlad. En haut de ses routes tortueuses trône la résidence du seigneur de la vallée, Duinhir, et de sa femme Rosfin. Quand le Gondor est entré en guerre, le seigneur Duinhir et ses fils Duilin et Derufin ont mené cinq cents archers à Minas Tirith afin de défendre la ville contre les armées du Mordor. À la fin de la guerre, quand le seigneur Duinhir et ses hommes sont retournés à Morlad, la nouvelle de la mort de ses fils et de la tentative de lui imputer le meurtre d'un émissaire Haradrim se répandit. Malgré la victoire du Gondor, Morlad est devenu un endroit à l'atmosphère maussade, et nombre de ses habitants pensent avoir été abandonnés par le roi Elessar au lendemain de la Guerre de l'Anneau."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "56.84S"; 
          ["LONG"] = "67.55W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Alagrant"; 
      ["PROGRESS"] = "Trouvez Alagrant"; 
      ["LORE"] = "Situé dans la forêt à l'ouest de la Colline d'Erech, le hameau d'Alagrant est principalement composé de chasseurs et de marchands de la Vallée de la Racine Noire."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "58.61S"; 
          ["LONG"] = "68.59W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Tingobel"; 
      ["PROGRESS"] = "Trouvez Tingobel"; 
      ["LORE"] = "Bien qu'il ait été pillé par les Orques des Montagnes Blanches durant la Guerre de l'Anneau, le village minier de Tingobel a été repris sur ordre du seigneur Duinhir. Les demeures touchées ont depuis été réparées et la mine restaurée, mais nul habitant de la Vallée de la Racine Noire n'a oublié les vies qui ont été perdues en ce lieu."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "56.13S"; 
          ["LONG"] = "64.04W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Sardol"; 
      ["PROGRESS"] = "Trouvez Sardol"; 
      ["LORE"] = "Au sud d'Alagrant, le long de la rivière Morthond, se trouve Sardol, un hameau peuplé de pêcheurs et de fermiers. Avant que les Héritiers de Castamir n'érigent le blocus aujourd'hui ravagé sur la route menant à la Couronne de Tarlang, Sardol servait de petit comptoir marchand pour les voyageurs circulant entre Morlad et Dol Amroth."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "60.97S"; 
          ["LONG"] = "69.74W"; 
        }; 
      }; 
      };
      [6] = {
