  -- Les îles du Bouclier / Habitants des îles du Bouclier
  [1879469676] = {
      [1] = {
      ["NAME"] = "La fin du voyage"; 
      ["PROGRESS"] = "Trouvez la Fin du voyage"; 
      ["LORE"] = "Les eaux qui bordent Zîrar Tarka sont périlleuses pour les âmes ignorantes, mais il arrive même aux marins les plus aguerris de s'aventurer trop près des îles lorsque des tempêtes surviennent dans la baie de Belfalas, à leur plus grand désarroi. Quand la chance des Valar leur sourit, certains survivent pour voir les premiers rayons du soleil transpercer la canopée de la Fin du voyage. Dans le cas contraire, seule la coque de leur navire subsiste, flottant près de l'archipel et ornant à jamais le sourire de Shedax Khîn, les Mâchoires broyeuses."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "0.72S"; 
          ["LONG"] = "130.19W"; 
        }; 
      }; 
      };
      [2] = {
      ["NAME"] = "Halrax"; 
      ["PROGRESS"] = "Trouvez Halrax"; 
      ["LORE"] = "La vaste bourgade de Halrax se situe sur Zamarzîr, la plus grande des îles qui composent l'archipel de Zîrar Tarka. Elle a été bâtie au fil de nombreux printemps par les flibustiers de la baie de Belfalas, et bien que la plupart de ses habitants vont et viennent toutes les quelques années, un point reste immuable : chacun prise sa liberté et son individualité avant tout et ne répond qu'à lui-même. Halrax est devenu un refuge où se rassemblent parfois à leur insu les âmes égarées n'ayant d'autre endroit où aller."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "10.00S"; 
          ["LONG"] = "126.30W"; 
        }; 
      }; 
      };
      [3] = {
      ["NAME"] = "Kafagur"; 
      ["PROGRESS"] = "Trouvez Kafagur"; 
      ["LORE"] = "Les résidents de Zîrar Tarka pensent que l'île septentrionale de Jeb Khâtel est inhabitée, et les voix orques qui s'élèvent des hautes falaises de ce lieu les encouragent bien peu à s'y rendre. Néanmoins, Kafagur est devenu le nouveau foyer d'un certain nombre de voyageurs inattendus s'étant aventurés bien loin de leurs terres ancestrales."; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "0.55N"; 
          ["LONG"] = "135.10W"; 
        }; 
      }; 
      };
      [4] = {
      ["NAME"] = "Phadâl Bimai"; 
      ["PROGRESS"] = "Trouvez Phadâl Bimai"; 
      ["LORE"] = "'Écoutez-moi bien, l'ami / amie', lance prudemment Manadhon en remplissant une chope de bière quasiment jusqu'à ras bord. Le tavernier de Halrax garde les yeux rivés sur vous, mais sa technique est si bien rodée que pas une goutte n'est gaspillée.\n\n'Nul ne voyage aussi loin au nord. Voulez-vous savoir pourquoi ?'\n\nVous hochez la tête.\n\n'En umbari, Phadâl Bimai signifie 'Naufrage des brutes'. Croyez-moi, vous feriez mieux de rester ici. Cet endroit jouit d'une dangereuse réputation.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "5.34N"; 
          ["LONG"] = "136.72W"; 
        }; 
      }; 
      };
      [5] = {
      ["NAME"] = "Aklâradum"; 
      ["PROGRESS"] = "Trouvez Aklâradum"; 
      ["LORE"] = "'Où avez-vous entendu ce nom ?', demande Manadhon. Vous lui indiquez que vous avez entendu quelqu'un y faire allusion, et le tavernier de Halrax réfléchit un moment.\n\n'Je ne me suis jamais rendu là-haut, mais lorsque la créature qui y vit a survolé le village et que l'ombre de ses ailes imposantes s'est abattue sur moi, j'en ai eu des frissons dans le dos. À mon avis, c'est un mauvais présage et vous feriez mieux d'oublier le nom d'Aklâradum. C'est là-bas que vit la créature, et tant qu'elle reste chez elle, nous pouvons vivre en paix. En tout cas… c'est ce que j'ai toujours cru.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "12.10S"; 
          ["LONG"] = "125.48W"; 
        }; 
      }; 
      };
      [6] = {
      ["NAME"] = "Irkhadâr"; 
      ["PROGRESS"] = "Trouvez Irkhadâr"; 
      ["LORE"] = "'Il est difficile de voyager sur l'île de Zamarzîr sans aussitôt apercevoir la grande caldeira à proximité de notre village', dit Manadhon d'un ton songeur, face au crépuscule. 'Vous savez, l'endroit n'a pas toujours été un lac. On raconte que c'était autrefois un volcan… Espérons qu'il reste endormi. Mais ce nom… parfois, je me demande s'il renferme une part de vérité.'\n\nVous demandez à Manadhon de quel nom il parle et il vous regarde dans les yeux.\n\n'Irkhadâr', répond-il. 'En umbari, cela signifie le 'Dormeur'.'"; 
      ["POINTS"] = {
        [1] = {
          ["LAT"] = "11.65S"; 
          ["LONG"] = "126.00W"; 
        }; 
      }; 
      };
  };
