      [2] = {
      ["NAME"] = "Khataj Mârsa"; 
      ["PROGRESS"] = "Trouvez Khataj Mârsa"; 
      ["LORE"] = "Les corsaires ont élu domicile ici à Khataj Mârsa, ce qui leur offre un accès facile aux îles au nord-ouest qui composent Bakhjâd ainsi qu'à la vaste île de Zamarzîr au sud-est."; 
      ["POINTS"] = {
